module body() {
    // Create flowing serpentine body with overlapping scales
    for(i = [0:10]) {
        angle = i * 20;
        y_pos = -i * 8;
        x_pos = sin(angle) * 5;
        z_pos = cos(angle * 0.7) * 3;

        translate([x_pos, y_pos, z_pos]) {
            // Main body segment - ellipsoid for smooth flow
            scale([1.2, 1, 0.8]) sphere(r = 6 - i * 0.3);

            // Scale plates on top
            for(j = [0:3]) {
                rotate([0, 0, j * 90]) {
                    translate([0, 0, 3]) 
                    scale([1, 0.4, 0.2]) 
                    sphere(r = 4);
                }
            }
        }
    }
}

module wings() {
    // Mercury droplet wings - liquid metal that flows upward
    for(side = [-1, 1]) {
        translate([side * 15, 0, 0]) {
            rotate([0, side * 20, 0]) {
                // Main wing membrane
                difference() {
                    // Droplet shape
                    scale([1.5, 0.3, 2]) sphere(r = 12);

                    // Hollow center for thin walls
                    translate([0, -2, 0]) 
                    scale([1.3, 0.1, 1.8]) 
                    sphere(r = 12);
