// CosmicCoil - Mythical space serpent with nebula wings and star patterns
// 80mm long, designed for FDM printing with no supports

// Main parameters
body_length = 80;
body_radius = 6;
wing_span = 40;
neck_length = 15;
head_length = 12;

// Colors
color_body = "navy";
color_wings = "purple";
color_accents = "gold";
color_stars = "white";

module star(diameter) {
    // Simple 6-point star primitive
    union() {
        cylinder(h = 0.4, d = diameter, $fn = 6);
        rotate([0, 0, 30])
            cylinder(h = 0.4, d = diameter * 0.7, $fn = 6);
    }
}

module nebula_wing() {
    color(color_wings)
    difference() {
        // Main wing membrane
        union() {
            scale([1, 0.8, 0.05])
                sphere(d = wing_span);
            // Wing extensions
            for (i = [0:3]) {
                rotate([0, 0, i * 25 - 37])
                    translate([wing_span/2 - 5, 0, 0])
                        scale([1.2, 0.6, 0.2])
                            sphere(d = 10);
            }
        }

        // Cut holes for nebula pattern
        for (i = [0:5]) {
            rotate([0, 0, i * 20])
                translate([wing_span/2 - 10, 0, 0])
                    cylinder(h = 2, d = 6, $fn = 8);
        }
    }
}

module cosmic_body_segment(t) {
    // Tapered body segment with scales
    radius = body_radius * (1 - t * 0.3);
    color(color_body)
    union() {
        // Main segment
        cylinder(h = 5, d = radius * 2);

        // Scales
        color(color_accents)
        for (angle = [0:30:330]) {
            rotate([0, 0, angle])
                translate([radius * 0.8, 0, 2.5])
                    scale([0.3, 0.2, 0.3])
                        sphere(d = 2);
        }
    }
}

module head() {
    color(color_body)
    union() {
        // Snout
        translate([0, 0, head_length/2])
            scale([1, 0.7, 1])
                sphere(d = head_length);

        // Horns
        color(color_accents)
        for (side = [-1, 1]) {
            translate([side * 3, 0, head_length - 2])
                rotate([0, side * 30, 0])
                    cylinder(h = 8, d1 = 2, d2 = 0.5);
        }

        // Eyes
        color("black")
        for (side = [-1, 1]) {
            translate([side * 2.5, head_length/2, head_length/2])
                sphere(d = 1.5);
        }
    }
}

module star_pattern() {
    color(color_stars)
    for (i = [0:12]) {
        angle = i * 30;
        radius = body_radius * 1.5 + sin(i * 2) * 2;
        translate([cos(angle) * radius, sin(angle) * radius, i * 5])
            star(1.5 + sin(i * 3) * 0.5);
    }
}

// Main assembly
union() {
    // Body coil
    for (i = [0:15]) {
        angle = i * 25;
        radius = 15 - i * 0.8;
        translate([cos(angle) * radius, sin(angle) * radius, i * 5])
            rotate([0, 0, angle + 90])
                cosmic_body_segment(i / 15.0);
    }

