// Cometdrake - A mythical celestial dragon with comet-like features
// Wings with star patterns, fiery tail, and crystalline horns

$fn = 32;  // Smooth curves for 3D printing

// Main colors
color_drake = [0.1, 0.2, 0.4];      // Deep space blue
color_fire = [1, 0.3, 0];           // Fiery orange-red
color_crystal = [0.7, 0.9, 1];      // Ice crystal blue
color_stars = [1, 1, 0.8];          // Starlight yellow

// Overall scale for 3D printing
scale_factor = 1.5;

module cometdrake() {
    scale([scale_factor, scale_factor, scale_factor]) {

        // Main body - elongated teardrop shape
        union() {
            // Central body
            color(color_drake)
            translate([0, 0, 10])
            scale([1, 0.6, 2])
            sphere(10);

            // Head section
            color(color_drake)
            translate([0, 0, 30])
            scale([0.8, 0.6, 1.2])
            sphere(8);

            // Snout
            color(color_drake)
            translate([0, 0, 38])
            rotate([0, 20, 0])
            scale([0.4, 0.3, 1])
            cylinder(h=8, r1=3, r2=1);

            // Chest scales pattern
            for(i = [0:5]) {
                color([color_drake[0]+0.1, color_drake[1]+0.1, color_drake[2]+0.1])
                rotate([0, 0, i*60])
                translate([5, 0, 25-i*2])
                scale([1, 0.8, 0.3])
                sphere(2);
            }
        }

        // Wings - comet-like with star patterns
        module wing(side) {
            mirror([side, 0, 0]) {
                // Main wing membrane
                color(color_drake)
                translate([-10, 0, 25])
                rotate([0, -30, 0])
                scale([2, 3, 0.2])
                sphere(5);

                // Wing struts
                for(i = [1:3]) {
                    color([color_drake[0]*0.8, color_drake[1]*0.8, color_drake[2]*0.8])
                    translate([-5-i*3, 0, 25-i*3])
                    rotate([0, -20-i*5, 0])
                    scale([0.1, 0.1, 1.5])
                    cylinder(h=10, r=0.5);
                }

                // Star patterns on wings
                for(i = [0:12]) {
                    angle = i*30;
                    radius = 3 + sin(i*5)*1.5;
                    color(color_stars)
                    translate([-8 + cos(angle)*radius, sin(angle)*radius, 26])
                    sphere(0.5);
                }
            }
        }

        wing(1);   // Right wing
        wing(-1);  // Left wing

        // Horns - crystalline formations
        module horn(x_pos, z_pos, angle) {
            color(color_crystal)
            translate([x_pos, 0, z_pos])
            rotate([0, angle, 0])
            cylinder(h=8, r1=2, r2=0.5);

            // Horn crystals
            for(i = [0:3]) {
                color([color_crystal[0], color_crystal[1]-i*0.1, color_crystal[2]])
                translate([x_pos + sin(i*45)*0.5, 0, z_pos + i*2])
                rotate([0, angle, i*30])
                scale([0.3, 0.3, 0.3])
                cube([1, 1, 2], center=true);
            }
        }

        horn(4, 35, -30);   // Right horn
        horn(-4, 35, -30);  // Left horn

        // Comet tail - segmented with fire effect
        module tail_segment(pos, size) {
            color([color_fire[0], color_fire[1]*pos/5, color_fire[2]*pos/5])
            translate([0, 0, 10-pos*5])
            scale([size, size, size])
            sphere(3+pos);

            // Tail flame wisps
            for(i = [0:4]) {
                color([1, 0.5+pos*0.1, 0.2])
                rotate([0, i*72, 0])
                translate([0, 0, 10-pos*5])
                scale([0.5, 0.5, 2+pos])
                cylinder(h=3, r1=1, r2=0.1);
            }
        }

        for(i = [1:5]) {
            tail_segment(i, 1-i*0.15);
        }

        // Legs - sturdy for landing
        module leg(side, front) {
            translate([side*5, front*3, 5])
            rotate([0, 0, side*10])
            union() {
                // Upper leg
                color(color_drake)
                rotate([45, 0, 0])
                cylinder(h=6, r=1.5);

                // Lower leg
                color(color_drake)
                translate([0, 4, -2])
                rotate([30, 0, 0])
                cylinder(h=5, r=1.2);

                // Foot
                color(color_drake)
                translate([0, 6, -5])
                scale([2, 1, 0.5])
                sphere(1.5);
            }
        }

        leg(1, 1);   // Front right
        leg(-1, 1);  // Front left
        leg(1, -1);  // Back right
        leg(-1, -1); // Back left

        // Eyes - glowing orbs
        module eye(x_pos) {
            color(color_stars)
            translate([x_pos, 3, 34])
            sphere(1);

            // Pupil
            color([0, 0, 0])
            translate([x_pos*1.1, 3.5, 35])
            sphere(0.5);
        }

        eye(2);   // Right eye
        eye(-2);  // Left eye

        // Celestial markings on body
        for(i = [0:8]) {
            angle = i*45;
            color([color_stars[0], color_stars[1], color_stars[2], 0.7])
            translate([cos(angle)*6, sin(angle)*4, 20+sin(i*2)*3])
            sphere(0.8);
        }
    }
}

