// Blackholewyrm - A cosmic serpent that devours light
// Features: Event horizon coils, gravitational wings, void eyes, spacetime tail

$fn = 32;  // Smooth curves for 3D printing
thickness = 2;  // Wall thickness for printing

module body_coil(radius, height, turns) {
    // Create spiral coils representing spacetime distortion
    for (i = [0:5:360*turns]) {
        angle = i;
        x = radius * cos(angle);
        z = height * (i / (360*turns)) - height/2;
        y = radius * sin(angle);

        translate([x, y, z])
            rotate([0, 0, angle])
            sphere(r=thickness * 1.5);
    }
}

module gravitational_wings() {
    // Wings that bend spacetime around the wyrm
    color("darkblue", 0.7)
    for (side = [-1, 1]) {
        translate([side * 15, 0, 5])
            rotate([0, side * 30, 0])
            scale([1, 0.3, 0.7])
            sphere(r=12);

        // Wing membrane supports
        for (i = [0:30:120]) {
            translate([side * 15, 0, 5])
                rotate([i, side * 30, 0])
                cylinder(h=15, r1=thickness, r2=1);
        }
    }
}

module void_eyes() {
    // Eyes that absorb all light
    color("black")
    for (side = [-1, 1]) {
        translate([side * 8, 15, 0])
            scale([1, 1, 0.5])
            sphere(r=3);

        // Event horizon ring around eyes
        color("purple", 0.5)
        translate([side * 8, 15, 0])
            rotate_extrude()
            translate([2.5, 0, 0])
            circle(r=0.5);
    }
}

module accretion_jaw() {
    // Jaw that creates gravitational lensing
    color("darkpurple")
    translate([0, 20, 0])
        scale([1, 1.2, 0.6])
        sphere(r=8);

    // Inner void
    difference() {
        translate([0, 20, 0])
            scale([1, 1.2, 0.6])
            sphere(r=7);
        translate([0, 21, 0])
            scale([0.8, 0.8, 0.8])
            sphere(r=6);
    }
}

module spacetime_tail() {
    // Tail that dissipates into spacetime
    color("darkblue")
    for (i = [0:10:100]) {
        scale_factor = 1 - (i / 100);
        translate([0, -20 - i, sin(i*3) * 2])
            scale([scale_factor, scale_factor, scale_factor])
            sphere(r=6 * scale_factor);
    }
}

module event_horizon_scales() {
    // Scales that distort reality
    color("black", 0.8)
    for (i = [0:20:360]) {
        angle = i;
        x = 12 * cos(angle);
        y = 12 * sin(angle);

        translate([x, y, 0])
            rotate([90, 0, angle])
            scale([1, 0.3, 0.5])
            cylinder(h=3, r1=2, r2=1);
    }
}

