module spine_segment(height, radius) {
    union() {
        cylinder(h=height, r=radius, center=true);
        // Spine ridges
        for(i=[-1:1]) {
            translate([i*radius*0.6, 0, height/2])
                cube([bone_thickness, radius*0.3, height*0.2], center=true);
        }
    }
}

module vertebrae(count, length) {
    for(i=[0:count-1]) {
        color(bone_color)
        translate([0, 0, -i*length/count])
            spine_segment(length/count*0.8, bone_thickness*1.2);
    }
}

module rib_cage() {
    union() {
        // Central spine
        vertebrae(6, 25);

        // Ribs
        for(i=[1:6]) {
            rotate([0, i*15-45, 0])
                translate([0, 0, -i*4])
                    color(bone_color)
                    scale([1, 0.8, 0.3])
                        sphere(d=15);
