    union() {
        // Main skull
        scale([1.2, 0.8, 1])
            sphere(d=15);

        // Snout
        translate([8, 0, -2])
            scale([1.5, 0.6, 0.6])
                sphere(d=8);

        // Eye sockets
        difference() {
            sphere(d=15);
            translate([4, 6, 2])
                sphere(d=4);
            translate([4, -6, 2])
                sphere(d=4);
