        translate([0, 0, 8])
        scale([1.2, 0.8, 1])
        sphere(d = body_len);

        // Serpentine neck & head
        translate([-body_len/2, 0, 12])
        rotate([0, 20, 0])
        cylinder(h = 18, d1 = 10, d2 = 6);

