        rotate([0, 25, 0])
        cylinder(h = 10, d1 = 5, d2 = 2);

        // Tail
        for(i = [0:4]) {
            translate([body_len/2 + i*6, 0, 8 - i*1.5])
            rotate([0, -i*8, 0])
            scale([1 - i*0.15, 1 - i*0.15, 1 - i*0.1])
            sphere(d = body_len - i*5);
        }

        // Wings
        color("darkslateblue")
        for(side = [-1, 1]) {
            translate([0, side*body_len/3, 12])
            rotate([0, 0, side*-20])
