// Main body module
module rotfang_body() {
    color(color_body)
    union() {
        // Head
        translate([0, 0, 25])
        scale([1.2, 0.8, 0.8])
        sphere(8);

        // Neck
        translate([0, 0, 18])
        rotate([0, 10, 0])
        cylinder(h=10, r1=6, r2=4);

        // Torso
        translate([0, 0, 10])
        scale([1.5, 1, 1.2])
        sphere(10);

        // Tail segments
        for(i = [0:5]) {
            translate([-i*4 - 5, 0, 10 - i*2])
            scale([1 - i*0.15, 1 - i*0.15, 1 - i*0.15])
            rotate([0, -i*5, 0])
            sphere(8 - i);
        }
    }
}

// Wings with membrane structure
module rotfang_wings() {
    color(color_wing)
    union() {
        // Left wing
        translate([0, -15, 20])
        rotate([0, 0, 20])
        union() {
            // Main wing bone
            rotate([0, -30, 0])
            cylinder(h=25, r1=2, r2=1);

            // Wing membrane
            translate([0, -5, 0])
            rotate([0, 30, 0])
            scale([1, 0.8, 0.1])
            sphere(15);

            // Wing claws
            translate([20, -20, 5])
            rotate([0, 45, 0])
            cylinder(h=8, r=0.8);
        }

        // Right wing (mirrored)
        mirror([0, 1, 0])
        children();
    }
}

// Crystalline fangs
module rotfang_fangs() {
    color(color_fang)
    union() {
        // Upper fangs
        translate([6, 3, 25])
        rotate([0, 30, 0])
        cylinder(h=8, r1=1.5, r2=0.5);

        translate([6, -3, 25])
        rotate([0, 30, 0])
        cylinder(h=8, r1=1.5, r2=0.5);

        // Lower fangs
        translate([6, 2, 20])
        rotate([0, -30, 0])
        cylinder(h=6, r1=1.2, r2=0.4);

        translate([6, -2, 20])
        rotate([0, -30, 0])
        cylinder(h=6, r1=1.2, r2=0.4);
    }
}

// Eyes with magical glow
module rotfang_eyes() {
    color(color_eye)
    union() {
        translate([7, 3, 27])
        sphere(1.5);

        translate([7, -3, 27])
        sphere(1.5);
    }
}

// Scales pattern on back
module rotfang_scales() {
    color(color_body)
    difference() {
        // Scale base
        translate([0, 0, 15])
        scale([1.3, 0.9, 0.3])
        sphere(12);

        // Create scale pattern
        for(i = [0:8]) {
            translate([-i*2, 0, 16])
            rotate([0, i*20, 0])
            scale([0.8, 0.5, 0.2])
            sphere(3);
        }
    }
}

// Legs with claws
module rotfang_legs() {
    color(color_body)
    union() {
        // Front legs
        translate([5, 8, 15])
        rotate([0, 0, -30])
        union() {
            cylinder(h=8, r=2);
            translate([0, 0, -4])
            rotate([0, 0, 45])
            cylinder(h=6, r=1.5);
        }

        translate([5, -8, 15])
        rotate([0, 0, 30])
        children();

        // Back legs
        translate([-10, 6, 8])
        rotate([0, 0, -45])
        union() {
            cylinder(h=6, r=1.8);
            translate([0, 0, -3])
            rotate([0, 0, 60])
            cylinder(h=5, r=1.2);
        }

        translate([-10, -6, 8])
        rotate([0, 0, 45])
        children();
    }
}

