// Slimewyrm - A mythical acid-dwelling serpent with translucent gel body and bioluminescent spines
// Designed for 3D printing at ~80mm length

$fn = 32;

module body_segment(radius, length, spine_count) {
    // Main gel body segment
    color([0.4, 0.8, 0.3, 0.7]) 
    difference() {
        cylinder(h = length, r = radius);

        // Internal cavity for material saving
        translate([0, 0, 1])
            cylinder(h = length - 2, r = radius - 1.5);
    }

    // Bioluminescent spines
    color([0.1, 1, 0.6]) 
    for(i = [0:spine_count-1]) {
        rotate([0, 0, i * (360/spine_count)]) 
            translate([radius * 0.8, 0, length/2])
                rotate([90, 0, 0])
                    cylinder(h = radius * 0.6, r1 = 0.5, r2 = 0.1);
    }
}

module head() {
    // Main head bulb
    color([0.35, 0.75, 0.25, 0.8]) 
    sphere(d = 12);

    // Acid glands visible through translucent skin
    color([0.9, 1, 0.2, 0.5]) 
    translate([3, 2, 3]) sphere(d = 3);
    translate([3, -2, 3]) sphere(d = 3);

    // Mandibles
    color([0.2, 0.5, 0.1]) {
        translate([5, 3, -2]) rotate([0, 20, 0])
            cube([6, 1.5, 2]);
        translate([5, -3, -2]) rotate([0, -20, 0])
            cube([6, 1.5, 2]);
    }

    // Eye stalks
    color([0.1, 0.9, 0.4, 0.9]) {
        translate([-2, 4, 4]) rotate([0, 30, 0])
            cylinder(h = 5, r1 = 1.5, r2 = 1);
        translate([-2, -4, 4]) rotate([0, -30, 0])
            cylinder(h = 5, r1 = 1.5, r2 = 1);
    }

    // Eyes
    color([1, 0.2, 0.8]) {
        translate([-2, 4.5, 5.5]) sphere(d = 1.5);
        translate([-2, -4.5, 5.5]) sphere(d = 1.5);
    }
}

module tail_fin() {
    // Translucent tail fin
    color([0.5, 0.9, 0.4, 0.6]) 
    translate([0, 0, -2])
        rotate([90, 0, 0])
            cylinder(h = 8, r1 = 0.1, r2 = 6);
}

// Main assembly
union() {
    // Head
    translate([0, 0, 0]) head();

    // Body segments - tapering towards tail
    for(i = [1:10]) {
        translate([0, 0, -i * 6])
            body_segment(
                radius = 6 - (i * 0.4), 
                length = 5, 
                spine_count = 8 + (i % 2) * 2
