    translate([0, 0, -65]) tail_fin();

    // Ventral ridge for stability
    color([0.3, 0.7, 0.2, 0.6])
    for(i = [0:10]) {
        translate([0, 0, -i * 6 + 2])
            rotate([0, 0, 0])
                cube([12 - i, 1, 3], center = true);
    }
}

