                // Wing membrane cutout
                translate([0, 1, 0])
                scale([0.7, 2.5, 0.7])
                sphere(5, $fn=8);
            }
        }

        // Armored scales along back
        color(color_scales)
        for(i = [0:3:40]) {
            translate([0, -35 + i * 0.8, 5 + sin(i * 10) * 2])
            rotate([0, 0, i * 5])
            scale([0.5, 1, 0.8])
