// Desertwyrm - a burrowing serpentine dragon of arid wastes
// Overall length  85 mm

$fn = 32;                       // smooth curves for 3D printing
scale_factor = 1;               // easy resizing
wall_thickness = 1.2;           // printable walls

module body_segment(r, h, taper = 1) {
    // one ridged ring of the wyrm's body
    difference() {
        cylinder(h = h, r1 = r, r2 = r * taper);
        translate([0, 0, -0.1])
            cylinder(h = h + 0.2, r1 = r - wall_thickness, r2 = (r - wall_thickness) * taper);
    }
}

module dorsal_spike(len, ang) {
    // single curved back-spike
    rotate([ang, 0, 0])
        scale([0.6, 0.6, 1])
            cylinder(h = len, r1 = 1.5, r2 = 0);
}

module scale_pattern(base_r) {
    // raised hexagonal scales on the hide
    for(a = [0:60:359])
        rotate([0, 0, a])
            translate([base_r * 0.9, 0, 0])
                sphere(r = 0.8);
}

module head() {
    // snout, jaws, horns
    union() {
        // main head
        color("BurlyWood")
        translate([0, 0, -5])
            scale([1.5, 1, 1])
                sphere(r = 8);

        // upper jaw
        color("SandyBrown")
        translate([0, 0, -7])
            rotate([30, 0, 0])
                cube([14, 6, 4], center = true);

        // lower jaw
        color("SandyBrown")
        translate([0, 0, -10])
            rotate([-20, 0, 0])
                cube([12, 5, 3], center = true);

        // horns
        color("SaddleBrown")
        translate([6, 0, 2])
            rotate([0, 30, 0])
                cylinder(h = 12, r1 = 2, r2 = 0);
        translate([-6, 0, 2])
            rotate([0, -30, 0])
                cylinder(h = 12, r1 = 2, r2 = 0);

        // eyes
        color("DarkRed")
        translate([4, -5, -2])
            sphere(r = 1.5);
        translate([-4, -5, -2])
            sphere(r = 1.5);
    }
}

module tail_spade() {
    // venomous tail blade
    color("DarkGoldenrod")
    translate([0, 0, 5])
        scale([1, 0.4, 1])
            cylinder(h = 12, r1 = 6, r2 = 0);
}

module desertwyrm() {
    scale(scale_factor)
    union() {
        // head
        translate([0, 0, 0])
            head();

        // neck segments
        neck_start = 9;
        for(i = [0:3]) {
            translate([0, 0, neck_start + i * 3])
                color("Tan")
                body_segment(8 - i * 0.7, 3, 0.9);
        }

        // main body rings
        body_start = neck_start + 4 * 3;
        body_len = 15;
        for(i = [0:body_len - 1]) {
            translate([0, 0, body_start + i * 3])
                color("Tan")
                union() {
                    body_segment(5.5 - i * 0.15, 3, 0.95);
                    // dorsal spikes every 3rd segment
                    if(i % 3 == 0)
                        for(ang = [-30, 0, 30])
                            translate([0, 0, 1])
                                dorsal_spike(4, ang);
                    // raised scales
                    translate([0, 0, 1.5])
                        scale_pattern(5.5 - i * 0.15);
                }
        }

        // tail segments tapering
        tail_start = body_start + body_len * 3;
        for(i = [0:7]) {
            translate([0, 0, tail_start + i * 2.5])
                color("Tan")
                body_segment(4 - i * 0.4, 2.5, 0.8);
        }

        // tail spade
        translate([0, 0, tail_start + 7 * 2.5 + 2])
            tail_spade();
    }
}

