// Size: ~80mm long, designed for 3D printing with no overhangs

// Main colors
$fa = 2;
$fs = 0.5;

module fernserpent() {
    color([0.2, 0.4, 0.1]) {
        union() {
            // Main body segments
            body();

            // Fern-like wings
            translate([-5, 0, 8]) rotate([0, 45, 0]) left_wing();
            translate([-5, 0, 8]) mirror([1, 0, 0]) left_wing();

            // Head with horns
            translate([-35, 0, 5]) head();

            // Vine tail
            translate([30, 0, 0]) tail();

            // Scales along body
            scales();
        }
    }
}

module body() {
    // Create segmented body using spheres and cylinders
    for(i = [0:5]) {
        translate([i * 8 - 25, 0, 3 * sin(i * 30)]) {
            // Body segment
            sphere(d = 12 - i, $fn = 16);

            // Connection between segments
            if(i < 5) {
                translate([4, 0, 0]) 
                    cylinder(h = 8, d1 = 10 - i, d2 = 12 - i - 1, $fn = 12);
            }
        }
    }
}

module left_wing() {
    // Main wing structure
    difference() {
        // Wing membrane
        linear_extrude(height = 0.5) {
            polygon(points = [
                [0, 0], [15, 0], [20, 8], [18, 15], [10, 20], [5, 18], [0, 10]
            ]);
        }

        // Fern leaf cuts
        for(i = [1:3]) {
            translate([i * 5, 5 + i * 3, -0.1]) 
                rotate([0, 0, -20 * i]) 
                    cylinder(h = 1, d = 3, $fn = 6);
        }
    }

    // Wing veins
    color([0.1, 0.2, 0.05]) {
        for(i = [0:2]) {
            translate([i * 6, 0, 0.3]) 
                rotate([0, 0, 30 * i]) 
                    cylinder(h = 0.5, d = 0.5, h = 15 - i * 3);
        }
    }
}

module head() {
    // Main head
    color([0.15, 0.35, 0.08]) {
        sphere(d = 10, $fn = 16);

        // Snout
        translate([-8, 0, 0]) 
            scale([1.5, 0.8, 0.8]) 
                sphere(d = 6, $fn = 12);

        // Eyes
        color([0.8, 0.9, 0.3]) {
            translate([-3, 3, 2]) sphere(d = 2);
            translate([-3, -3, 2]) sphere(d = 2);
        }

        // Horns
        color([0.3, 0.5, 0.1]) {
            translate([0, 4, 4]) 
                rotate([30, 0, 30]) 
                    cylinder(h = 8, d1 = 3, d2 = 1, $fn = 6);
            translate([0, -4, 4]) 
                rotate([30, 0, -30]) 
                    cylinder(h = 8, d1 = 3, d2 = 1, $fn = 6);
        }
    }
}

module tail() {
    // Tapering tail with vine pattern
    for(i = [0:8]) {
        translate([i * 4, 0, -i * 0.5]) {
            // Tail segment
            sphere(d = 8 - i * 0.7, $fn = 12);

            // Vine wrapping
            color([0.25, 0.45, 0.15]) {
                rotate_extrude(angle = 360, $fn = 20) {
                    translate([4 - i * 0.3, 0, 0]) 
                        square([0.5, 2 - i * 0.1]);
                }
            }

            // Connection cylinders
            if(i < 8) {
                translate([2, 0, 0]) 
                    cylinder(h = 2, d1 = 8 - i * 0.7, d2 = 8 - (i + 1) * 0.7, $fn = 10);
            }
        }
    }
}

module scales() {
    // Hexagonal scales along body
    color([0.18, 0.38, 0.12]) {
        for(i = [0:4]) {
            for(j = [0:3]) {
                translate([i * 8 - 25, 
                          (j - 1.5) * 3 * cos(i * 45), 
                          6 + 2 * sin(i * 30)]) {
                    rotate([90, 0, 0]) 
                        cylinder(h = 0.5, d = 2, $fn = 6);
                }
            }
        }
    }
}

