    translate([pos, side * 6, -3])
    color("forestgreen")
    union() {
        // Upper leg
        rotate([0, 0, side * 15])
            cylinder(h = 8, r = 2);

        // Lower leg
        translate([0, side * 4, -6])
            rotate([0, 0, side * 30])
            cylinder(h = 6, r = 1.5);

