fin_height = 8;
horn_height = 15;
scale_size = 1.5;

// Colors
serpent_color = [0.1, 0.6, 0.4]; // Deep emerald
fin_color = [0.2, 0.8, 0.7];   // Aqua
horn_color = [0.9, 0.9, 0.7];  // Pearl

module head() {
    color(serpent_color)
    union() {
        // Main head shape
        translate([0, 0, head_size/2])
            scale([1.2, 0.8, 1])
                sphere(d=head_size, $fn=32);

        // Snout
        translate([head_size/2, 0, 0])
            scale([1.5, 0.6, 0.6])
                sphere(d=head_size*0.8, $fn=24);

        // Eyes
        color([0, 0, 0])
        for(x = [-1, 1]) {
            translate([head_size/3, x*head_size/3, head_size/3])
                sphere(d=head_size/6, $fn=16);
        }

        // Mystical horn
        color(horn_color)
        translate([0, 0, head_size*0.8])
            cylinder(h=horn_height, d1=horn_height/3, d2=horn_height/10, $fn=8);
    }
}

module segment(width, height, length) {
    color(serpent_color)
    hull() {
        translate([0, 0, 0])
            sphere(d=width, $fn=16);
        translate([length, 0, 0])
            sphere(d=width*0.9, $fn=16);
    }
}

module dorsal_fin() {
    color(fin_color)
    difference() {
        // Main fin
        translate([0, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=fin_height, d1=fin_height/2, d2=0, $fn=3);

        // Cutouts for webbing
        for(i = [1:3]) {
            translate([0, i*fin_height/4, 0])
                rotate([0, 90, 0])
                    cylinder(h=fin_height*0.9, d1=fin_height/3, d2=0, $fn=3);
        }
    }
}

module scales() {
    color(serpent_color)
    for(i = [0:5]) {
        for(j = [0:8]) {
            rotate([0, j*40, 0])
                translate([i*scale_size*2, 0, 0])
                    rotate([90, 0, 0])
                        cylinder(h=scale_size/3, d=scale_size, $fn=6);
        }
    }
}

module tail_segment(pos, width) {
    translate(pos)
        rotate([0, 0, pos[0]*3])
            segment(width, width, width*1.2);
}

module body() {
    union() {
        // Generate serpentine body
        segments = 12;
        for(i = [0:segments-1]) {
            t = i/segments;
            x = i*body_len/segments;
            y = sin(t*360)*8;
            z = cos(t*180)*3;
            w = head_size*(1-t) + tail_width*t;

            tail_segment([x, y, z], w);

            // Add dorsal fins every few segments
            if(i % 3 == 0 && i > 1) {
                translate([x, y, z+w/2])
                    rotate([0, 0, -90])
                        dorsal_fin();
            }

            // Add scales
            if(i > 1) {
                translate([x, y, z-w/2])
                    scale([w/head_size, w/head_size, w/head_size])
                        scales();
            }
        }
    }
}

module side_fins() {
    color(fin_color)
    for(side = [-1, 1]) {
        translate([body_len/3, side*head_size/2, -head_size/4])
            rotate([0, 90, 0])
                difference() {
                    cylinder(h=head_size, d1=head_size, d2=head_size/2, $fn=3);
                    translate([0, 0, -1])
                        cube([head_size*2, head_size, head_size*2], center=true);
                }
    }
}

// Main assembly
translate([-body_len/2, 0, head_size/2])
    union() {
        head();
        translate([head_size*0.8, 0, 0])
            body();
        side_fins();
    }

