// Hippogriff OpenSCAD Model - Realistic Style, Medium Complexity
// Wingspan: 80mm, Length: 70mm, Height: 45mm

module hippogriff() {
    // Main body - eagle-lion hybrid
    color([0.8, 0.6, 0.3]) union() {
        // Lion body
        translate([0, 0, 15]) 
            scale([1.2, 0.8, 1]) 
            sphere(r=12, $fn=32);

        // Eagle chest
        translate([10, 0, 22])
            scale([1, 1.2, 0.9])
            sphere(r=10, $fn=32);

        // Back ridge
        for(i=[-5:2:5]) {
            translate([i*2, 0, 28-i])
                cube([1.5, 8, 3], center=true);
        }
    }

    // Head - eagle features
    color([0.9, 0.7, 0.4]) union() {
        // Main head shape
        translate([25, 0, 25])
            scale([1.5, 1.2, 1.3])
            sphere(r=8, $fn=32);

        // Beak
        translate([35, 0, 25])
            rotate([0, -20, 0])
            cylinder(h=12, r1=3, r2=0.5, $fn=16);

        // Eyes
        color([0.1, 0.1, 0.1])
        for(x=[-3, 3]) {
            translate([30, x, 28])
                sphere(r=1.5, $fn=12);
        }

        // Head feathers
        for(i=[0:15:45]) {
            translate([20+sin(i)*3, cos(i)*6, 32])
                rotate([0, -i, 0])
                scale([0.3, 0.1, 1])
                sphere(r=4, $fn=8);
        }
    }

    // Wings - eagle wings
    color([0.7, 0.5, 0.2]) union() {
        // Left wing
        translate([-5, -15, 25])
            rotate([0, 0, -30])
            difference() {
                union() {
                    // Main wing
                    scale([1, 3, 0.5])
                    sphere(r=8, $fn=32);

                    // Primary feathers
                    for(i=[0:3]) {
                        translate([-5-i*3, -20-i*2, 0])
                            rotate([0, 0, -10*i])
                            scale([0.8, 1.2, 0.3])
                            sphere(r=4, $fn=8);
                    }
                }

                // Wing detail cuts
                for(i=[0:2]) {
                    translate([0, -10-i*5, 0])
                        cube([2, 6, 2], center=true);
                }
            }

        // Right wing (mirrored)
        mirror([0, 1, 0])
            translate([-5, 15, 25])
                rotate([0, 0, 30])
                difference() {
                    union() {
                        scale([1, 3, 0.5])
                        sphere(r=8, $fn=32);

                        for(i=[0:3]) {
                            translate([-5-i*3, 20+i*2, 0])
                                rotate([0, 0, 10*i])
                                scale([0.8, 1.2, 0.3])
                                sphere(r=4, $fn=8);
                        }
                    }

                    for(i=[0:2]) {
                        translate([0, 10+i*5, 0])
                            cube([2, 6, 2], center=true);
                    }
                }
    }

    // Lion legs
    color([0.6, 0.4, 0.2]) union() {
        // Front legs
        for(side=[-1, 1]) {
            translate([10, side*8, 10])
                union() {
                    // Upper leg
                    cylinder(h=12, r=4, $fn=16);

                    // Lower leg
                    translate([0, 0, -12])
                        cylinder(h=10, r=3, $fn=16);

                    // Paw
                    translate([0, 0, -22])
                        scale([1.2, 1.2, 0.8])
                        sphere(r=4, $fn=16);
                }
        }

        // Hind legs
        for(side=[-1, 1]) {
            translate([-12, side*6, 8])
                rotate([10, 0, 0])
                union() {
                    cylinder(h=10, r=4, $fn=16);
                    translate([0, 0, -10])
                        cylinder(h=8, r=3, $fn=16);
                    translate([0, 0, -18])
                        scale([1.3, 1.3, 0.8])
                        sphere(r=4, $fn=16);
                }
        }
    }

    // Lion tail with eagle feathers
    color([0.5, 0.3, 0.1]) union() {
        // Tail
        translate([-20, 0, 15])
            rotate([20, 0, 0])
            cylinder(h=15, r=2, $fn=16);

        // Tail feathers
        color([0.7, 0.5, 0.2])
        for(i=[0:3]) {
            translate([-30-i*2, sin(i*30)*3, 15-i])
                rotate([0, 0, sin(i*30)*10])
                scale([0.5, 1.5, 0.2])
                sphere(r=3, $fn=8);
        }
    }

    // Claws
    color([0.3, 0.3, 0.3])
    for(side=[-1, 1]) {
        // Front claws
        translate([10, side*8, -22])
            for(i=[-1, 0, 1]) {
                translate([i*1.5, 0, -2])
                    scale([0.3, 0.3, 1])
                    cylinder(h=3, r=1, $fn=8);
            }

        // Hind claws
        translate([-12, side*6, -18])
            for(i=[-1, 0, 1]) {
                translate([i*1.5, 0, -2])
                    scale([0.3, 0.3, 1])
                    cylinder(h=3, r=1, $fn=8);
            }
    }

    // Eagle talons on wings
    color([0.3, 0.3, 0.3])
    for(side=[-1, 1]) {
        translate([-5, side*15, 15])
            for(i=[0:2]) {
                translate([0, side*3-i*2, -2])
                    scale([0.3, 0.3, 1])
                    cylinder(h=2.5, r=0.8, $fn=8);
            }
    }
}

