union() {
    base();
    kraken_body();
    kraken_eyes();
    kraken_beak();
    kraken_tentacles();
    suction_cups();
}

// Leviathan - Mythical Sea Serpent
// Realistic style, medium complexity

// Main body parameters
body_length = 80;
body_width = 15;
body_height = 20;

// Color definitions
leviathan_body = [0, 0.2, 0.3];      // Deep ocean blue-green
leviathan_accents = [0.1, 0.4, 0.5]; // Lighter scales
leviathan_eyes = [1, 0, 0];        // Glowing red eyes

// Scale module for repetitive patterns
module scale_pattern(size, count) {
    for(i = [0:count-1]) {
        translate([i*size*0.8, 0, sin(i*30)*0.5]) {
            color(leviathan_accents)
            scale([1, 0.8, 0.3])
            sphere(size/2);
        }
    }
}

// Horn module
module horn(height, radius) {
    color(leviathan_accents)
    cylinder(h=height, r1=radius, r2=radius/3);
}

// Fin module
module fin(width, height, thickness) {
    color(leviathan_accents)
    difference() {
        scale([1, 0.3, 1])
        sphere([width, height, thickness]);

        translate([0, -height, 0])
        cube([width*2, height*2, thickness*2], center=true);
    }
}

// Main leviathan model
module leviathan() {
    // Main body
    color(leviathan_body)
    union() {
        // Central torso
        translate([0, 0, 0])
        scale([body_length/2, body_width/2, body_height/2])
        sphere(1);

        // Head
        translate([-body_length/2 - 10, 0, body_height/4])
        rotate([0, 30, 0])
        scale([1.2, 0.8, 0.8])
        sphere(8);

        // Long serpentine tail
        for(i = [0:8]) {
            translate([i*body_length/10, 0, -i*1.5])
            rotate([0, 0, sin(i*20)*10])
            scale([1 - i*0.08, 1 - i*0.05, 1 - i*0.05])
            sphere(body_width/2);
        }

        // Dorsal fins
        for(i = [1:3]) {
            translate([-body_length/3 + i*body_length/3, 0, body_height/2 + 2])
            rotate([0, 0, 0])
            fin(8, 6, 2);
        }

        // Side fins
        translate([body_length/4, body_width/2 + 3, -body_height/4])
        rotate([0, 0, 90])
        fin(6, 4, 1.5);

        mirror([0, 1, 0])
        translate([body_length/4, body_width/2 + 3, -body_height/4])
        rotate([0, 0, 90])
        fin(6, 4, 1.5);

        // Horns
        translate([-body_length/2 - 15, 0, body_height/2 + 5])
        rotate([0, -30, 0])
        horn(8, 2);

        translate([-body_length/2 - 15, -3, body_height/2 + 3])
        rotate([0, -30, -20])
        horn(6, 1.5);

        translate([-body_length/2 - 15, 3, body_height/2 + 3])
        rotate([0, -30, 20])
        horn(6, 1.5);

        // Eyes
        color(leviathan_eyes)
        translate([-body_length/2 - 12, -3, body_height/2 + 2])
        sphere(1.5);

        color(leviathan_eyes)
        translate([-body_length/2 - 12, 3, body_height/2 + 2])
        sphere(1.5);

        // Scales along body
        for(i = [0:3]) {
            translate([-body_length/3 + i*body_length/4, 0, body_height/2])
            scale_pattern(3, 5);
        }

        // Tail scales
        for(i = [0:5]) {
            translate([i*body_length/8, 0, -i*1.5 - body_height/4])
            scale_pattern(2, 3);
        }

        // Mouth
        color([0.1, 0.1, 0.1])
        translate([-body_length/2 - 15, 0, 0])
        rotate([0, 30, 0])
        difference() {
            sphere(6);
