// Gargouille - Mythical Water-Spouting Dragon
// Realistic style, medium complexity

// Main parameters
scale_factor = 0.7;
body_length = 80 * scale_factor;
body_height = 40 * scale_factor;
wing_span = 100 * scale_factor;

// Colors
stone_color = [0.5, 0.5, 0.45];
accent_color = [0.3, 0.3, 0.25];

// Main body
module gargouille_body() {
    color(stone_color)
    union() {
        // Torso
        translate([0, 0, body_height/2])
            scale([1.5, 1, 1])
            sphere(r=body_height/2, $fn=32);

        // Chest plate
        translate([body_length/4, 0, body_height/2])
            scale([1.2, 0.8, 0.6])
            sphere(r=body_height/2.5, $fn=32);

        // Back ridge
        for(i = [-1:0.5:1]) {
            translate([i*body_length/3, 0, body_height])
                rotate([0, 0, i*20])
                scale([0.3, 0.3, 1])
                cylinder(h=body_height/3, r=body_height/8, $fn=6);
        }
    }
}

// Head
module gargouille_head() {
    color(stone_color)
    union() {
        // Main head
        translate([-body_length/2, 0, body_height*1.2])
            scale([1.2, 0.8, 1])
            sphere(r=body_height/2.2, $fn=32);

        // Snout
        translate([-body_length/2-body_height/2, 0, body_height*1.1])
            scale([1.5, 0.5, 0.5])
            sphere(r=body_height/3, $fn=32);

        // Horns
        for(angle = [30, 150]) {
            translate([-body_length/2-body_height/4, 0, body_height*1.5])
                rotate([0, angle, 0])
                cylinder(h=body_height/2, r1=body_height/8, r2=body_height/20, $fn=8);
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([-body_length/2-body_height/3, side*body_height/4, body_height*1.3])
                color([0, 0, 0])
                sphere(r=body_height/15, $fn=16);
        }

        // Water spout
        difference() {
            translate([-body_length/2-body_height/1.5, 0, body_height])
                rotate([90, 0, 0])
                cylinder(h=body_height/2, r=body_height/10, $fn=16);
            translate([-body_length/2-body_height/1.5, 0, body_height-body_height/2])
                cube([body_height/5, body_height/5, body_height], center=true);
        }
    }
}

// Wings
module gargouille_wing() {
    color(accent_color)
    difference() {
        // Main wing membrane
        translate([0, wing_span/4, body_height*0.8])
            rotate([0, 0, 30])
            scale([2, 1, 0.1])
            sphere(r=wing_span/4, $fn=32);

        // Wing holes for Gothic style
        for(i = [0.3, 0.6, 0.9]) {
            translate([wing_span/2*i, wing_span/4*i, 0])
                scale([0.3, 0.3, 2])
                sphere(r=wing_span/10, $fn=16);
        }
    }
}

// Tail
module gargouille_tail() {
    color(stone_color)
    hull() {
        translate([body_length/2, 0, body_height/2])
            sphere(r=body_height/3, $fn=16);
        translate([body_length, 0, body_height/4])
            rotate([0, 0, 45])
            scale([0.5, 0.5, 2])
            cylinder(h=body_height, r=body_height/6, $fn=8);
    }

    // Tail spikes
    for(i = [0.2, 0.4, 0.6, 0.8]) {
        translate([body_length/2 + (body_length/2)*i, 0, body_height/2 - body_height/4*i])
            rotate([0, 0, 45])
            scale([0.3, 0.3, 1])
            cylinder(h=body_height/5, r=body_height/12, $fn=4);
    }
}

// Legs
module gargouille_leg(side) {
    color(stone_color)
    union() {
        // Upper leg
        translate([body_length/4, side*body_height/3, body_height/4])
            rotate([0, side*15, 0])
            cylinder(h=body_height/2, r=body_height/8, $fn=16);

        // Lower leg
        translate([body_length/4+body_height/4, side*body_height/2.5, 0])
            rotate([0, side*30, 0])
            cylinder(h=body_height/2, r=body_height/10, $fn=16);

        // Foot
        translate([body_length/4+body_height/3, side*body_height/2, -body_height/4])
            scale([1.5, 0.8, 0.5])
            sphere(r=body_height/10, $fn=16);
    }
}

// Main assembly
union() {
    gargouille_body();
    gargouille_head();

    // Wings
    gargouille_wing();
    mirror([0, 1, 0]) gargouille_wing();

    gargouille_tail();

    // Legs
    gargouille_leg(1);
    gargouille_leg(-1);
}

