                        rotate([0, i * -10, 0])
                        scale([0.8, 0.1, 3])
                        cube([15, 1, 8], center=true);
                }
        }

        // Right wing
        translate([25, 0, 45]) {
            rotate([0, -30, 30])
                mirror([1, 0, 0])
                // Main wing structure
                for(i = [0:2]) {
                    translate([i * -8, 0, i * 3])
                        rotate([0, i * -10, 0])
