module hippocampus() {
    // Main body - horse front with fish tail
    color("SeaGreen") {
        // Horse head
        translate([0, 0, 15]) {
            rotate([0, 0, 15]) {
                sphere(8);
                // Muzzle extension
                translate([7, 0, -2]) {
                    scale([1.5, 0.8, 0.8]) sphere(6);
                }
            }
        }

        // Neck
        translate([0, 0, 8]) {
            cylinder(h=10, r1=6, r2=5);
        }

        // Front body - horse torso
        translate([0, 0, 0]) {
            scale([1.2, 0.8, 1]) sphere(10);
        }

        // Fish tail - main section
        translate([-15, 0, 0]) {
            scale([2, 0.6, 0.4]) sphere(8);

            // Tail fin segments
            for(i = [1:3]) {
                translate([-20 - i*5, 0, 0]) {
                    rotate([0, 0, -i*10]) {
                        scale([3-i*0.5, 0.3, 0.1]) sphere(5);
                    }
                }
            }
        }
    }

    // Dorsal fin
    color("DarkCyan") {
        translate([-10, 0, 8]) {
            rotate([0, 90, 0]) {
                linear_extrude(height=0.5) {
                    polygon(points=[
                        [0, 0], [3, 5], [6, 0], [3, 3], [0, 0]
                    ]);
                }
            }
        }
    }

    // Pectoral fins
    module fin(x_pos, y_sign) {
        translate([x_pos, y_sign*8, 0]) {
            rotate([0, 0, y_sign*30]) {
                color("Teal") {
                    for(j = [0:2]) {
                        translate([j*2, 0, 0]) {
                            rotate([0, 0, -j*20]) {
                                scale([0.3, 0.8, 0.1]) sphere(4);
                            }
                        }
                    }
                }
            }
        }
    }

    fin(-8, 1);
    fin(-8, -1);

    // Eyes
    color("Black") {
        translate([5, 3, 15]) sphere(1.5);
        translate([5, -3, 15]) sphere(1.5);
    }

    // Mane - horse hair flowing into fins
    color("ForestGreen") {
        for(k = [0:4]) {
            translate([0, 4*cos(k*45), 12 - k*2]) {
                rotate([0, 0, k*10]) {
                    scale([0.3, 0.3, 2]) sphere(2);
                }
            }
            translate([0, -4*cos(k*45), 12 - k*2]) {
                rotate([0, 0, -k*10]) {
                    scale([0.3, 0.3, 2]) sphere(2);
                }
            }
        }
    }

    // Scales pattern
    color("MediumAquamarine") {
        for(row = [0:3]) {
            for(col = [0:5]) {
                translate([-5 - col*2, 0, row*2 - 2]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=0.5, r=1.5, $fn=6);
                    }
                }
            }
        }
    }

    // Tail fin rays
    color("DarkTurquoise") {
        for(angle = [-30:15:30]) {
            translate([-25, 0, 0]) {
                rotate([0, 0, angle]) {
                    cylinder(h=15, r1=0.5, r2=0.2);
                }
            }
        }
    }
}

// Create the creature
scale([scale_factor, scale_factor, scale_factor]) {
    hippocampus();
}

// Huldra - Scandinavian forest spirit
// Realistic medium complexity OpenSCAD model

$fn = 50;
scale_factor = 0.5; // Scales entire model to ~75mm height

module huldra() {
    scale([scale_factor, scale_factor, scale_factor]) {

        // Colors
        color("burlywood") {
            // Head
            translate([0, 0, 150]) {
                difference() {
                    sphere(20);
                    // Eye sockets
                    translate([-8, -10, 5]) rotate([0, 20, 0]) 
                        cylinder(h=15, r1=5, r2=0);
                    translate([8, -10, 5]) rotate([0, -20, 0]) 
                        cylinder(h=15, r1=5, r2=0);
                }

                // Eyes
                color("black") {
                    translate([-8, -12, 8]) sphere(4);
                    translate([8, -12, 8]) sphere(4);
                }

                // Nose
                color("burlywood") translate([0, -15, 0]) sphere(3);

                // Mouth
                color("darkred") 
                    translate([0, -18, -3]) 
                    rotate([90, 0, 0]) cylinder(h=0.5, r=5);

                // Ears (slightly pointed)
                color("burlywood") {
                    translate([-15, 0, 5]) rotate([90, 0, 30]) 
                        cylinder(h=15, r1=5, r2=2);
                    translate([15, 0, 5]) rotate([90, 0, -30]) 
                        cylinder(h=15, r1=5, r2=2);
                }
            }

            // Torso
            translate([0, 0, 100]) 
                cylinder(h=50, r1=25, r2=20);

            // Arms
            color("burlywood") {
                translate([-30, 0, 110]) rotate([0, 0, 30]) 
                    cylinder(h=35, r=7);
                translate([30, 0, 110]) rotate([0, 0, -30]) 
                    cylinder(h=35, r=7);
            }

            // Hands
            translate([-45, 0, 90]) sphere(8);
            translate([45, 0, 90]) sphere(8);
        }

        // Bark-like skin texture on back
        color("saddlebrown") {
            difference() {
                translate([0, 12, 100]) 
                    cube([40, 5, 50], center=true);

                // Bark texture holes
                for(i = [0:2]) {
                    for(j = [0:4]) {
                        translate([-15 + i*15, 12, 75 + j*10]) 
                            sphere(3);
                    }
                }
            }
        }

        // Hollow back (tree trunk)
        color("saddlebrown") {
            translate([0, 15, 100]) 
                difference() {
                    cube([35, 10, 45], center=true);
                    translate([0, 5, 0]) 
                        cube([25, 10, 35], center=true);
                }
        }

        // Long flowing hair
        color("goldenrod") {
            translate([0, -20, 150]) 
                for(i = [0:20]) {
                    rotate([0, i*18, 0]) 
                        translate([0, 0, -i*2]) 
                            cylinder(h=80 - i*2, r1=3, r2=1);
                }
        }

        // Dress
        color("forestgreen") {
            translate([0, 0, 50]) 
                cylinder(h=50, r1=30, r2=40);
        }

        // Legs (hidden under dress)
        color("burlywood") {
            translate([-10, 0, 20]) cylinder(h=40, r=8);
            translate([10, 0, 20]) cylinder(h=40, r=8);
        }

        // Cow tail (huldra characteristic)
        color("black") {
            translate([0, 25, 30]) 
                rotate([0, 0, 45]) 
                    cylinder(h=30, r=5);
            translate([0, 35, 35]) 
                rotate([0, 0, 45]) 
                    cylinder(h=25, r=4);
            translate([0, 45, 40]) 
                rotate([0, 0, 45]) 
                    cylinder(h=20, r=3);
            translate([0, 55, 45]) 
                sphere(4);
        }

        // Feet
        translate([-10, 0, 0]) {
            color("burlywood") sphere(10);
            color("black") 
                translate([0, 0, -8]) 
                    cube([15, 25, 5], center=true);
        }
        translate([10, 0, 0]) {
            color("burlywood") sphere(10);
            color("black") 
                translate([0, 0, -8]) 
                    cube([15, 25, 5], center=true);
        }
    }
}

// Base for stability
color("darkgreen") 
    translate([0, 0, -15]) 
        cylinder(h=10, r=50);

// Generate the huldra
huldra();

// Ifrit - Fire Djinn in Realistic Style - Medium Complexity
// Size: 80mm tall when printed

// Main parameters
body_height = 40;
body_width = 25;
chest_depth = 20;
head_size = 18;
horn_length = 15;
wing_span = 60;
tail_length = 35;
arm_length = 25;
leg_length = 30;

// Colors
color_ifrit = [0.9, 0.3, 0.1];      // Fiery orange-red
color_eyes = [1, 0.8, 0];          // Glowing yellow
color_fire = [1, 0.4, 0];         // Brighter fire

// Main module
module ifrit() {
    union() {
        // Body core
        translate([0, 0, leg_length]) {
            color(color_ifrit) {
                // Torso
                difference() {
                    // Main body
                    scale([body_width/20, chest_depth/20, body_height/20])
                        sphere(d=40);

                    // Chest indentation
                    translate([0, chest_depth/2, 0])
                        cube([body_width*0.6, chest_depth*0.3, body_height*0.4], center=true);
                }

                // Abdomen
                translate([0, 0, -body_height/3])
                    cylinder(h=body_height/2, r1=body_width/2.5, r2=body_width/3, center=true);
            }
        }

        // Head
        translate([0, 0, leg_length + body_height + head_size/2]) {
            color(color_ifrit) {
                // Main head
                sphere(d=head_size);

                // Jaw
                translate([0, head_size/3, -head_size/3])
                    cube([head_size*0.8, head_size*0.6, head_size*0.5], center=true);

                // Horns - curved back
                for(side = [-1, 1]) {
                    translate([side*head_size/2, 0, head_size/3])
                        rotate([0, side*20, 0])
                        rotate([30, 0, 0])
                        cylinder(h=horn_length, r1=3, r2=1);
                }

                // Eyes - glowing orbs
                for(side = [-1, 1]) {
                    color(color_eyes)
                    translate([side*head_size/3, head_size/2.5, head_size/4])
                        sphere(d=4);
                }
            }
        }

        // Arms
        for(side = [-1, 1]) {
            translate([side*body_width/2, 0, leg_length + body_height*0.7]) {
                rotate([0, 0, side*30])
                rotate([90, 0, 0])
                color(color_ifrit) {
                    // Upper arm
                    cylinder(h=arm_length/2, r=4);

                    // Lower arm
                    translate([0, 0, arm_length/2 + 2])
                        cylinder(h=arm_length/2, r=3);

                    // Clawed hand
                    translate([0, 0, arm_length + 2])
                        sphere(d=6);
                }
            }
        }

        // Legs
        for(side = [-1, 1]) {
            translate([side*body_width/3, 0, leg_length/2]) {
                color(color_ifrit) {
                    // Thigh
                    cylinder(h=leg_length/2, r1=6, r2=5);

                    // Lower leg
                    translate([0, 0, leg_length/2 + 2])
                        cylinder(h=leg_length/2, r1=5, r2=4);

                    // Hoof-like foot
                    translate([0, 0, leg_length + 2])
                        scale([1.5, 3, 1])
                        sphere(d=8);
                }
            }
        }

        // Wings - bat-like membrane
        for(side = [-1, 1]) {
            translate([side*body_width/2, -chest_depth/3, leg_length + body_height*0.8]) {
                rotate([0, 0, side*45])
                rotate([0, side*20, 0])
                color(color_ifrit) {
                    // Wing bones
                    for(i = [0:2]) {
                        rotate([0, 0, i*30*side])
                        translate([0, 0, -i*5])
                        cylinder(h=wing_span/3, r=2);
                    }

                    // Wing membrane
                    translate([0, 0, -5])
                    scale([1, wing_span/40, wing_span/40])
                    sphere(d=wing_span/2);
                }
            }
        }

        // Tail - segmented and fiery
        translate([0, -chest_depth/2, leg_length/2]) {
            color(color_ifrit) {
                // Tail segments getting smaller
                for(i = [0:4]) {
                    translate([0, -i*tail_length/5, -i*2])
                        cylinder(h=tail_length/5, r1=6-i, r2=5-i);
                }

                // Fiery tip
                color(color_fire)
                translate([0, -tail_length-5, -8])
                    sphere(d=8);
            }
        }

        // Fire aura around body
        for(i = [0:5]) {
            rotate([0, 0, i*60])
            color(color_fire, 0.3)
            translate([body_width/1.2, 0, leg_length + body_height/2])
                cylinder(h=body_height/2, r1=3, r2=1);
        }
    }
}

// Render the ifrit
ifrit();

// Imp Creature - Realistic Style
// Height: ~75mm when printed

// Color definitions
color_skin = [0.2, 0.4, 0.1];      // Muted green-grey skin
color_wings = [0.15, 0.15, 0.15];  // Dark leathery wings
color_horns = [0.4, 0.3, 0.2];     // Brown horns
color_eyes = [1, 0, 0];            // Glowing red eyes
color_claws = [0.2, 0.2, 0.2];     // Dark claws

// Main module
module imp_creature() {
    // Body
    color(color_skin)
    union() {
        // Torso
        translate([0, 0, 25])
        scale([1.2, 1, 1.5])
        sphere(d=20, $fn=16);

        // Abdomen
        translate([0, 0, 15])
        scale([0.8, 0.7, 1])
        sphere(d=18, $fn=16);

        // Head
        translate([0, 0, 45])
        scale([0.8, 0.7, 1])
        sphere(d=15, $fn=16);

        // Snout
        translate([0, 7, 43])
        rotate([15, 0, 0])
        scale([0.5, 0.6, 0.4])
        cylinder(h=8, d1=8, d2=6, $fn=8);

        // Arms
        // Left arm
        translate([-12, 0, 35])
        rotate([0, -20, 10])
        union() {
            // Upper arm
            cylinder(h=12, d=6, $fn=8);
            // Forearm
            translate([0, 0, -12])
            rotate([15, 0, 0])
            cylinder(h=10, d=5, $fn=8);
            // Hand
            translate([0, 3, -20])
            sphere(d=6, $fn=8);
        }

        // Right arm
        mirror([1, 0, 0])
        translate([-12, 0, 35])
        rotate([0, -20, 10])
        union() {
            // Upper arm
            cylinder(h=12, d=6, $fn=8);
            // Forearm
            translate([0, 0, -12])
            rotate([15, 0, 0])
            cylinder(h=10, d=5, $fn=8);
            // Hand
            translate([0, 3, -20])
            sphere(d=6, $fn=8);
        }

        // Legs
        // Left leg
        translate([-5, 0, 15])
        rotate([0, 10, 0])
        union() {
            // Thigh
            cylinder(h=15, d=7, $fn=8);
            // Lower leg
            translate([0, 0, -15])
            rotate([0, -15, 0])
            cylinder(h=12, d=6, $fn=8);
            // Foot
            translate([0, 4, -26])
            scale([1.2, 2, 0.6])
            sphere(d=8, $fn=8);
        }

        // Right leg
        mirror([1, 0, 0])
        translate([-5, 0, 15])
        rotate([0, 10, 0])
        union() {
            // Thigh
            cylinder(h=15, d=7, $fn=8);
            // Lower leg
            translate([0, 0, -15])
            rotate([0, -15, 0])
            cylinder(h=12, d=6, $fn=8);
            // Foot
            translate([0, 4, -26])
            scale([1.2, 2, 0.6])
            sphere(d=8, $fn=8);
        }

        // Tail
        translate([0, -12, 20])
        rotate([30, 0, 0])
        union() {
            for(i = [0:3]) {
                translate([0, -i*3, -i*2])
                scale([1 - i*0.15, 1 - i*0.15, 1])
                sphere(d=8 - i*2, $fn=12);
            }
        }

        // Wings
        color(color_wings)
        union() {
            // Left wing
            translate([-15, 0, 40])
            rotate([0, 0, -20])
            difference() {
                // Main wing membrane
                scale([2, 1, 0.1])
                sphere(d=25, $fn=16);
                // Wing holes
                for(i = [1:3]) {
                    translate([i*5 - 5, -5 + i*2, 0])
                    scale([0.5, 0.3, 1])
                    cylinder(h=2, d=3, $fn=6);
                }
            }

            // Wing bones
            for(i = [0:2]) {
                rotate([0, 0, -i*30])
                translate([-5 - i*5, 0, 0])
                scale([0.2, 0.2, 1])
                cylinder(h=20, d=2, $fn=6);
            }
        }

        // Right wing (mirrored)
        mirror([1, 0, 0])
        color(color_wings)
        union() {
            translate([-15, 0, 40])
            rotate([0, 0, -20])
            difference() {
                scale([2, 1, 0.1])
                sphere(d=25, $fn=16);
                for(i = [1:3]) {
                    translate([i*5 - 5, -5 + i*2, 0])
                    scale([0.5, 0.3, 1])
                    cylinder(h=2, d=3, $fn=6);
                }
            }

            for(i = [0:2]) {
                rotate([0, 0, -i*30])
                translate([-5 - i*5, 0, 0])
                scale([0.2, 0.2, 1])
                cylinder(h=20, d=2, $fn=6);
            }
        }
    }

    // Horns
    color(color_horns)
    union() {
        // Left horn
        translate([-3, 0, 52])
        rotate([0, -15, -10])
        cylinder(h=8, d1=3, d2=1, $fn=6);

        // Right horn
        translate([3, 0, 52])
        rotate([0, -15, 10])
        cylinder(h=8, d1=3, d2=1, $fn=6);
    }

    // Eyes
    color(color_eyes)
    union() {
        // Left eye
        translate([-3, 5, 48])
        sphere(d=2, $fn=8);

        // Right eye
        translate([3, 5, 48])
        sphere(d=2, $fn=8);
    }

    // Claws on hands and feet
    color(color_claws)
    union() {
        // Hand claws
        for(side = [-1, 1]) {
            mirror([side, 0, 0])
            translate([12, 0, 15])
            for(i = [0:2]) {
                rotate([0, 0, -30 + i*30])
                translate([0, 3, -20])
                rotate([0, -20, 0])
                cylinder(h=3, d1=1, d2=0.5, $fn=4);
            }
        }

        // Foot claws
        for(side = [-1, 1]) {
            mirror([side, 0, 0])
            translate([5, 2, 12])
            for(i = [-1, 0, 1]) {
                rotate([0, 0, i*20])
                translate([0, 4, -26])
                cylinder(h=2, d1=1.5, d2=0.5, $fn=4);
            }
        }
    }
}

// Generate the creature
imp_creature();

// Jackalope - Mythical Rabbit with Antlers
// Realistic style, medium complexity

// Overall dimensions: ~80mm length

module jackalope() {
    color("BurlyWood") {
        // Main body - slightly elongated rabbit body
        union() {
            // Torso
            translate([0, 0, 15])
                scale([1.2, 0.8, 1])
                sphere(r=15, $fn=32);

            // Chest
            translate([-10, 0, 10])
                scale([0.8, 0.6, 0.8])
                sphere(r=12, $fn=32);

            // Rump
            translate([18, 0, 12])
                scale([0.9, 0.8, 0.9])
                sphere(r=14, $fn=32);
        }

        // Head
        translate([-20, 0, 25])
            scale([0.9, 0.8, 1.1])
            sphere(r=12, $fn=32);

        // Snout
        translate([-30, 0, 22])
            scale([0.7, 0.6, 0.6])
            sphere(r=8, $fn=32);
