                // Branch 2
                translate([0, 0, 6])
                    rotate([0, -10, 30])
                    cylinder(h=12, r1=1.5, r2=0.8, $fn=6);
            }
        }
    }

    // Legs
    color("BurlyWood") {
        // Front legs
        for(side = [-1, 1]) {
            translate([-15, side*8, 8])
                rotate([0, 10, 0])
                cylinder(h=20, r=3, $fn=8);
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([15, side*8, 8])
                rotate([0, -10, 0])
                cylinder(h=20, r=3.5, $fn=8);
        }
    }

    // Tail - cotton tail
    color("White") {
        translate([28, 0, 18])
            sphere(r=5, $fn=16);
    }

    // Eyes
    color("Black") {
        // Left eye
        translate([-25, -5, 27])
            sphere(r=1.5, $fn=8);

        // Right eye
        translate([-25, 5, 27])
            sphere(r=1.5, $fn=8);
    }

    // Nose
    color("Black") {
        translate([-33, 0, 22])
            sphere(r=2, $fn=8);
    }

    // Whiskers
    color("DimGray") {
        for(angle = [0:120:240]) {
            rotate([0, 0, angle])
                translate([-30, 0, 21])
                    rotate([0, 90, 0])
                    cylinder(h=8, r=0.3, $fn=4);
        }
    }

    // Magical elements - glowing runes on antlers
    color("Gold") {
        // Left antler runes
        translate([-14, -12, 42]) {
            rotate([0, -30, 0])
                for(i = [0:2]) {
                    translate([0, 0, i*4])
                        sphere(r=0.5, $fn=6);
                }
        }

        // Right antler runes
        translate([-14, 12, 42]) {
            rotate([0, -30, 0])
                for(i = [0:2]) {
                    translate([0, 0, i*4])
                        sphere(r=0.5, $fn=6);
                }
        }
    }
}

