color_mane = [0.9, 0.8, 0.6];   // Golden tan
color_hooves = [0.3, 0.3, 0.3]; // Dark gray
color_horn = [0.9, 0.9, 0.8];   // Pearl white
color_scales = [0.6, 0.8, 0.7]; // Seafoam green
color_eyes = [0.8, 0.2, 0.2];   // Ruby red

// Main body
module kiirin_body() {
    color(color_body)
    union() {
        // Torso - elongated ellipsoid
        scale([1.3, 1, 0.8])
        sphere(d=body_width);

        // Chest extension
        translate([-body_length/4, 0, 0])
        scale([1.2, 0.9, 0.7])
        sphere(d=body_width);

        // Hindquarters
        translate([body_length/3, 0, -2])
        scale([1, 1.1, 0.9])
        sphere(d=body_width);
    }
}

// Leg module with hoof
module leg(height, offset_x, offset_y, angle=0) {
    color(color_body)
    translate([offset_x, offset_y, -body_height/2])
    rotate([0, angle, 0])
    union() {
        // Upper leg
        cylinder(h=height*2/3, d1=8, d2=6, $fn=8);

        // Lower leg
        translate([0, 0, -height*2/3])
        cylinder(h=height/3, d1=6, d2=5, $fn=8);

        // Hoof
        color(color_hooves)
        translate([0, 0, -height])
        cylinder(h=4, d1=5, d2=7, $fn=8);
    }
}

// Mane and tail hair strands
module hair_strand(length, thickness) {
    color(color_mane)
    rotate([90, 0, 0])
    cylinder(h=length, d=thickness, $fn=6);
}

// Scales pattern
module scales() {
    color(color_scales)
    for(i=[-20:5:20]) {
        for(j=[-10:5:10]) {
            if(i*i + j*j < 400) {
                translate([i, j, body_height/2 + 2])
                scale([1, 0.7, 0.3])
                sphere(d=3);
            }
        }
    }
}

// Head with horn
module kiirin_head() {
    translate([-body_length/2 - 10, 0, body_height/2 + 5])
    union() {
        // Head base
        color(color_body)
        scale([1.2, 0.8, 1])
        sphere(d=15);

        // Snout
        translate([-7, 0, -2])
        scale([1.5, 0.6, 0.7])
        sphere(d=10);

        // Eyes
        color(color_eyes)
        translate([-2, 6, 3])
        sphere(d=3);
        translate([-2, -6, 3])
        sphere(d=3);

        // Horn (spiral)
        color(color_horn)
        for(i=[0:15:360]) {
            rotate([0, 0, i])
            translate([0, 0, i/20])
            rotate([90, 0, 0])
            cylinder(h=20, d1=3, d2=0.5, $fn=6);
        }

        // Mane on neck
        for(i=[0:10:30]) {
            rotate([0, i-15, 0])
            translate([0, 7, -2])
            hair_strand(10 + i/2, 1.5);
            translate([0, -7, -2])
            hair_strand(10 + i/2, 1.5);
        }
    }
}

// Tail
