        // Back legs
        for(side = [-1, 1]) {
            translate([-15, side * 6, 3]) {
                rotate([0, side * 20, 0])
                cylinder(h = 10, r = 2);

                translate([0, 0, -10])
                    rotate([0, side * 30, 0])
                    cylinder(h = 8, r = 1.5);

                translate([0, side * 2, -17])
                    scale([1.8, 0.2, 1.2])
                    sphere(3);
            }
        }
    }
}

module knucker_scales() {
    color("limegreen") {
        // Dorsal scales
        for(i = [0:8]) {
            translate([-5 - i * 6, 0, 15 + sin(i * 45) * 2])
                rotate([0, 0, i * 15])
                scale([0.5, 2, 0.1])
                cylinder(h = 3, r = 2);
        }

        // Side scales
        for(side = [-1, 1]) {
            for(i = [0:4]) {
