                    translate([-10 - i * 8, side * (7 + j * 2), 10 - j])
                        rotate([0, 90, 0])
                        scale([0.3, 0.3, 0.1])
                        cylinder(h = 2, r = 1.5);
                }
            }
        }
    }
}

// Assemble the knucker
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        knucker_body();
        knucker_head();
        knucker_wings();
        knucker_tail();
        knucker_legs();
        knucker_scales();
    }
