// Medusa - Realistic Style, Medium Complexity
// 3D printable model with proper wall thickness and no overhangs

// Global variables
$fn = 32;
scale_factor = 0.4;  // Scale to ~80mm height

// Color definitions
skin_color = "#deb887";
snake_color = "#556b2f";
hair_color = "#8b4513";
stone_color = "#708090";

// Main module
module medusa() {
    scale([scale_factor, scale_factor, scale_factor]) {
        // Body
        union() {
            // Head
            color(skin_color) head();

            // Torso
            color(skin_color) torso();

            // Snake hair
            color(snake_color) snake_hair();

            // Arms
            color(skin_color) left_arm();
            color(skin_color) right_arm();

            // Lower body (snake tail)
            color(snake_color) snake_body();

            // Face details
            face_details();
        }
    }
}

// Head module
module head() {
    translate([0, 0, 45]) {
        difference() {
            // Main head shape
            sphere(15);

            // Neck hole
            translate([0, 0, -15]) {
                cylinder(30, 5, 5);
            }
        }

        // Eyes
        translate([-5, 12, 3]) {
            color(stone_color) sphere(2);
        }
        translate([5, 12, 3]) {
            color(stone_color) sphere(2);
        }

        // Nose
        translate([0, 12, 0]) {
            scale([1, 1, 0.5]) sphere(2);
        }

        // Mouth
        translate([0, 10, -5]) {
            rotate([90, 0, 0]) cylinder(0.5, 3, 3);
        }
    }
}

// Torso module
module torso() {
    translate([0, 0, 25]) {
        // Upper body
        scale([8, 4, 20]) sphere(1);

        // Chest detail
        translate([0, 0, 5]) {
            scale([6, 3, 5]) sphere(1);
        }
    }
}

// Snake hair module
module snake_hair() {
    // Generate multiple snakes around the head
    for(i = [0:11]) {
        angle = i * 30;
        radius = 15;
        translate([0, 0, 45]) {
            rotate([0, 0, angle]) {
                translate([radius, 0, 0]) {
                    // Each snake
                    snake();
                }
            }
        }
    }
}

// Single snake module
module snake() {
    // Snake body segments
    translate([0, 0, 0]) {
        for(j = [0:5]) {
            translate([j * 3, sin(j * 30) * 2, cos(j * 30) * 2]) {
                scale([3, 1.5, 1.5]) sphere(1);
            }
        }

        // Snake head
        translate([18, 0, 0]) {
            scale([2, 1, 1]) sphere(1.5);

            // Eyes
            translate([0.5, 0.5, 0.5]) {
                color("red") sphere(0.3);
            }
            translate([0.5, -0.5, 0.5]) {
                color("red") sphere(0.3);
            }
        }
    }
}

// Left arm module
module left_arm() {
    translate([-10, 5, 30]) {
        rotate([0, 15, -20]) {
            // Upper arm
            cylinder(10, 2, 2);

            // Lower arm
            translate([0, 0, -10]) {
                rotate([0, 15, 0]) {
                    cylinder(8, 2, 1.5);

                    // Hand
                    translate([0, 0, -8]) {
                        sphere(2);
                    }
                }
            }
        }
    }
}

// Right arm module
module right_arm() {
    translate([10, 5, 30]) {
        rotate([0, -15, 20]) {
            // Upper arm
            cylinder(10, 2, 2);

            // Lower arm
            translate([0, 0, -10]) {
                rotate([0, -15, 0]) {
                    cylinder(8, 2, 1.5);

                    // Hand
                    translate([0, 0, -8]) {
                        sphere(2);
                    }
                }
            }
        }
    }
}

// Snake body module
module snake_body() {
    // Start from waist
    translate([0, 0, 15]) {
        // Coiled snake tail
        for(i = [0:20]) {
            angle = i * 20;
            radius = 15 - i * 0.5;
            height = 15 - i * 0.7;

            translate([cos(angle) * radius, sin(angle) * radius, height]) {
                scale([3 - i * 0.05, 3 - i * 0.05, 3 - i * 0.05]) sphere(1);
            }
        }

        // Tail tip
        translate([cos(21 * 20) * 5, sin(21 * 20) * 5, 0]) {
            scale([2, 2, 2]) sphere(1);
        }
    }
}

// Face details module
module face_details() {
    // Eyebrows
    translate([-7, 12, 5]) {
        scale([2, 1, 0.5]) sphere(1);
    }
    translate([7, 12, 5]) {
        scale([2, 1, 0.5]) sphere(1);
    }

    // Ears
    translate([-12, 0, 3]) {
        scale([0.5, 2, 3]) sphere(1);
    }
    translate([12, 0, 3]) {
        scale([0.5, 2, 3]) sphere(1);
    }
}

