                for(i=[0:30:330]) {
                    rotate([0, 0, i]) {
                        translate([8, 0, 0]) rotate([90, 0, 0]) cylinder(15, 1.5, 0.5);
                    }
                }
            }
        }

        // Torso
        color(skin_color) {
            hull() {
                translate([0, 0, 50]) scale([6, 4, 8]) sphere(1);
                translate([0, 0, 35]) scale([5, 3.5, 7]) sphere(1);
            }

            // Arms
            translate([-8, 6, 45]) rotate([0, 0, -20]) {
