                cylinder(12, 2, 1.5);
                translate([0, 0, 12]) rotate([0, 0, 30]) cylinder(10, 1.5, 1);
            }
        }

        // Waist transition
        color(skin_color) {
            translate([0, 0, 28]) scale([4.5, 3.5, 3]) sphere(1);
        }

        // Twin fish tails
        // Left tail
        color(tail_color) {
            translate([-4, 0, 25]) {
                // Upper tail segment
                hull() {
                    translate([0, 0, 0]) scale([2, 2, 8]) sphere(1);
                    translate([0, 0, -12]) scale([1.5, 1.5, 6]) sphere(1);
                }

                // Lower tail segment
                translate([0, 0, -20]) {
                    hull() {
                        translate([0, 0, 0]) scale([1.5, 1.5, 6]) sphere(1);
                        translate([0, 0, -10]) scale([1, 1, 8]) sphere(0.8);
                    }
                }

                // Tail fin
                color(fin_color) {
                    translate([0, 0, -38]) rotate([0, 0, -15]) {
                        difference() {
                            scale([8, 1, 6]) sphere(1);
                            translate([4, 0, -2]) scale([5, 2, 4]) sphere(1);
                        }
                    }
                }
            }

            // Right tail
            translate([4, 0, 25]) {
                // Upper tail segment
                hull() {
                    translate([0, 0, 0]) scale([2, 2, 8]) sphere(1);
                    translate([0, 0, -12]) scale([1.5, 1.5, 6]) sphere(1);
                }

                // Lower tail segment
                translate([0, 0, -20]) {
                    hull() {
                        translate([0, 0, 0]) scale([1.5, 1.5, 6]) sphere(1);
                        translate([0, 0, -10]) scale([1, 1, 8]) sphere(0.8);
                    }
                }

                // Tail fin
                color(fin_color) {
                    translate([0, 0, -38]) rotate([0, 0, 15]) {
                        difference() {
                            scale([8, 1, 6]) sphere(1);
                            translate([-4, 0, -2]) scale([5, 2, 4]) sphere(1);
                        }
                    }
                }
            }
        }

        // Scales pattern on tails
        color(tail_color) {
            for(z=[-5:-5:-30]) {
                for(a=[0:45:315]) {
                    translate([-4 + 0.5*cos(a), 0.5*sin(a), z]) {
