scale_factor = 0.5;  // Scale to ~75mm length

// Colors
body_color = "silver";
horn_color = "gold";
eye_color = "blue";
fin_color = "teal";

module monoceros() {
    // Main body - elongated dolphin-like form
    color(body_color)
    union() {
        // Torso
        translate([0, 0, 10])
        scale([2, 1, 0.8])
        sphere(15);

        // Tail section
        translate([-25, 0, 10])
        scale([2.5, 0.8, 0.6])
        sphere(12);

        // Head
        translate([20, 0, 15])
        scale([1.2, 0.9, 1.1])
        sphere(12);
    }

    // Single spiral horn
    color(horn_color)
    translate([28, 0, 25])
    rotate([0, 0, 0])
    cylinder(h1=18, h2=0, r1=2, r2=0.5, $fn=8);

    // Add spiral ridges to horn
    for(i=[0:30:360]) {
        color(horn_color)
        translate([28 + sin(i)*1.5, cos(i)*1.5, 25 + i/20])
        rotate([0, 0, i])
        scale([0.3, 0.3, 1])
        cylinder(h=2, r=0.5, $fn=6);
    }

    // Dorsal fin - shark-like
    color(fin_color)
    translate([-10, 0, 22])
    rotate([0, 0, 0])
    linear_extrude(height=0.1)
    polygon(points=[
        [0, 0],
        [-8, 12],
        [-16, 0],
        [-12, 15],
        [-8, 0]
    ]);

    // Side fins
    for(side=[-1, 1]) {
        color(fin_color)
        translate([5, side*12, 12])
        rotate([0, side*20, 0])
        scale([1, 0.3, 1])
        cylinder(h=10, r1=5, r2=2, $fn=6);
    }

    // Tail fluke
    color(fin_color)
    translate([-38, 0, 10])
    rotate([0, 90, 0])
    scale([1.5, 1, 0.3])
    sphere(8);

    // Eye
    color(eye_color)
    translate([25, 6, 18])
    sphere(2);

    // Nostril
    color(body_color)
    translate([27, 2, 16])
    sphere(1);

    // Scale pattern along body
    for(i=[-20:5:20]) {
        for(j=[0:60:300]) {
            color("lightgray")
            translate([i, 
                     15*sin(j) + sin(i*2)*2, 
                     10 + 15*cos(j) + cos(i*2)*2])
            scale([2, 1, 0.5])
            sphere(1.5);
        }
    }

    // Magical aura effect (subtle ridges)
    for(i=[-30:10:30]) {
        color("white", 0.3)
        translate([i, 0, 25 + sin(i)*3])
        rotate([0, 90, 0])
        cylinder(h=0.5, r=20 - abs(i)/2, $fn=16);
    }
}

