// Mothman - Realistic style, medium complexity
// Designed for 3D printing - 80mm tall

// Main parameters
$fn=60;
body_width = 20;
body_height = 35;
head_size = 12;
wing_span = 60;
leg_length = 25;

// Color definitions
dark_gray = [0.2, 0.2, 0.25];
red = [0.8, 0.1, 0.1];
black = [0.05, 0.05, 0.05];
eye_red = [1, 0, 0];

module mothman() {
    union() {
        // Body - segmented thorax and abdomen
        color(dark_gray)
        union() {
            // Thorax (upper body segment)
            translate([0, 0, leg_length + 5])
                scale([1.2, 0.8, 1.5])
                sphere(d=body_width);

            // Abdomen (lower body segment)
            translate([0, 0, leg_length - 5])
                scale([0.8, 0.6, 1.2])
                sphere(d=body_height);
        }

        // Head with red glowing eyes
        color(dark_gray)
        translate([0, 0, leg_length + 20])
            sphere(d=head_size);

        // Large red compound eyes
        color(eye_red)
        union() {
            translate([head_size/2.5, head_size/1.5, leg_length + 20])
                sphere(d=head_size/2);
            translate([-head_size/2.5, head_size/1.5, leg_length + 20])
                sphere(d=head_size/2);
        }

        // Antennae
        color(black)
        union() {
            translate([head_size/3, head_size/1.5, leg_length + 27])
                rotate([0, 30, 0])
                cylinder(h=15, d1=2, d2=0.5);
            translate([-head_size/3, head_size/1.5, leg_length + 27])
                rotate([0, -30, 0])
                cylinder(h=15, d1=2, d2=0.5);
        }

        // Large moth wings with eye patterns
        wings();

        // Six legs (moth-like)
        legs();

        // Small horns on head
        color(black)
        union() {
            translate([0, head_size/2, leg_length + 25])
                cylinder(h=5, d1=3, d2=1);
            translate([head_size/4, head_size/2.2, leg_length + 24])
                rotate([0, 10, 0])
                cylinder(h=4, d1=2, d2=1);
            translate([-head_size/4, head_size/2.2, leg_length + 24])
                rotate([0, -10, 0])
                cylinder(h=4, d1=2, d2=1);
        }
    }
}

module wings() {
    // Left wing
    color([0.15, 0.15, 0.2])
    translate([body_width/2, 0, leg_length + 15])
    rotate([0, 0, 45])
    scale([1, 0.1, 1])
    difference() {
        // Main wing shape
        scale([2.5, 1, 1])
        sphere(d=wing_span/2);

        // Cut wing into proper shape
        translate([0, -wing_span/2, 0])
            cube([wing_span, wing_span, wing_span], center=true);
    }

    // Right wing
    mirror([1, 0, 0])
    translate([body_width/2, 0, leg_length + 15])
    rotate([0, 0, 45])
    scale([1, 0.1, 1])
    difference() {
        scale([2.5, 1, 1])
        sphere(d=wing_span/2);

        translate([0, -wing_span/2, 0])
            cube([wing_span, wing_span, wing_span], center=true);
    }

    // Eye patterns on wings
    color(red)
    for(i = [-1, 1]) {
        translate([i * (body_width/2 + 15), 0, leg_length + 15])
        rotate([0, 0, i * 45])
        scale([1, 0.2, 1])
        difference() {
            sphere(d=10);
            sphere(d=8);
        }
    }
}

module legs() {
    // Front pair
    for(side = [-1, 1]) {
        color(dark_gray)
        translate([side * body_width/3, 0, leg_length])
        rotate([0, side * 20, 0])
        cylinder(h=leg_length, d=3);
    }

    // Middle pair
    for(side = [-1, 1]) {
        color(dark_gray)
        translate([side * body_width/2.5, 0, leg_length - 3])
        rotate([0, side * 15, 0])
        cylinder(h=leg_length + 2, d=3);
    }

    // Back pair
    for(side = [-1, 1]) {
        color(dark_gray)
        translate([side * body_width/3.5, 0, leg_length - 6])
        rotate([0, side * 10, 0])
        cylinder(h=leg_length + 4, d=3);
    }
}

