// Pard - Mythical spotted leopard-cheetah hybrid
// Realistic medium complexity version with 3D-printable features

// Main body parameters
body_length = 45;
body_height = 22;
body_width = 18;

// Head parameters
head_length = 15;
head_height = 12;
head_width = 10;

// Leg parameters
leg_height = 18;
leg_radius = 3;

// Tail parameters
tail_length = 35;
tail_segments = 8;

// Wing parameters
wing_span = 40;
wing_depth = 20;

// Colors
pard_body_color = [0.8, 0.6, 0.4];  // Sandy tan
pard_spot_color = [0.2, 0.15, 0.1];  // Dark brown
pard_wing_color = [0.9, 0.85, 0.8];  // Translucent membrane

module pard_body() {
    color(pard_body_color)
    union() {
        // Main torso
        translate([-body_length/4, 0, 0])
        scale([1.2, 1, 1])
        sphere(d=body_height);

        // Chest bulge
        translate([body_length/3, 0, -body_height/4])
        sphere(d=body_height*1.1);

        // Hindquarters
        translate([-body_length/2, 0, 0])
        scale([0.9, 1.1, 0.9])
        sphere(d=body_height*1.2);
    }
}

module pard_head() {
    color(pard_body_color)
    translate([body_length/2 + head_length/2, 0, body_height/2 + 5])
    union() {
        // Main head shape
        scale([1.3, 1, 0.8])
        sphere(d=head_height);

        // Snout extension
        translate([head_length/2, 0, -head_height/4])
        scale([0.6, 0.8, 0.5])
        sphere(d=head_height);

        // Ears
        translate([0, head_width/2, head_height/2])
        rotate([0, 30, 0])
        cylinder(h=head_height/2, r1=2, r2=0.5);

        translate([0, -head_width/2, head_height/2])
        rotate([0, -30, 0])
        cylinder(h=head_height/2, r1=2, r2=0.5);
    }
}

module pard_leg(position, angle) {
    translate(position)
    rotate([0, 0, angle])
    union() {
        // Upper leg
        color(pard_body_color)
        cylinder(h=leg_height*0.6, r1=leg_radius, r2=leg_radius*0.8);

        // Lower leg
        translate([0, 0, -leg_height*0.6])
        color(pard_body_color)
        cylinder(h=leg_height*0.4, r1=leg_radius*0.8, r2=leg_radius*0.6);

        // Paw
        translate([0, 0, -leg_height])
        color(pard_body_color)
        sphere(d=leg_radius*1.5);
    }
}

module pard_tail() {
    color(pard_body_color)
    translate([-body_length/2 - tail_length/2, 0, body_height/3])
    union() {
        for(i = [0:tail_segments-1]) {
            translate([-i*tail_length/tail_segments, 0, 0])
            scale([1 - i*0.05, 1 - i*0.05, 1 - i*0.05])
            sphere(d=body_height/3 * (1 - i*0.1));
        }
    }
}

module pard_wings() {
    color(pard_wing_color, 0.8)
    union() {
        // Left wing
        translate([0, body_width/2, body_height/2])
        rotate([0, 30, 0])
        difference() {
            scale([1.5, 0.1, 1])
            sphere(d=wing_span);
            translate([0, -2, 0])
            cube([wing_span*2, 5, wing_depth*2], center=true);
        }

        // Right wing
        translate([0, -body_width/2, body_height/2])
        rotate([0, -30, 0])
        mirror([0, 1, 0])
        difference() {
            scale([1.5, 0.1, 1])
            sphere(d=wing_span);
            translate([0, -2, 0])
            cube([wing_span*2, 5, wing_depth*2], center=true);
        }
    }
}

module pard_spots() {
    color(pard_spot_color)
    for(i = [0:20]) {
        theta = i * 137.5;  // Golden angle for natural distribution
        r = pow(i/20, 0.7) * body_length/2.2;
        x = cos(theta) * r;
        y = sin(theta) * r * 0.7;
        z = sin(i) * body_height/4 + body_height/2;

        translate([x, y, z])
        scale([0.3, 0.3, 0.1])
        sphere(d=body_height/5);
    }
}

// Main assembly
union() {
    pard_body();
    pard_head();
    pard_tail();
    pard_wings();

    // Legs - positioned for stability
    pard_leg([body_length/3, body_width/2 - 2, -body_height/2], 15);
    pard_leg([body_length/3, -body_width/2 + 2, -body_height/2], -15);
    pard_leg([-body_length/6, body_width/2 - 2, -body_height/2], -15);
    pard_leg([-body_length/6, -body_width/2 + 2, -body_height/2], 15);

    // Spots pattern
    pard_spots();
}

// Peryton - Mythical deer-bird creature
// Realistic style, medium complexity

$fn = 32;

// Main colors
color_body = "#8B4513";
color_wings = "#2F4F4F";
color_horns = "#DEB887";
color_eyes = "#FF0000";
color_beak = "#FFD700";

module peryton() {
    // Body - deer-like torso
    color(color_body) union() {
        // Main body
        rotate([90, 0, 0]) cylinder(h=30, d1=25, d2=20, center=true);

        // Chest
        translate([0, 0, 5]) sphere(d=22);

        // Neck
        translate([0, 0, 20]) cylinder(h=15, d=10);
    }

    // Head
    color(color_body) translate([0, 0, 30]) {
        // Skull structure
        rotate([90, 0, 0]) cylinder(h=20, d1=12, d2=8);

        // Snout - bird-like
        translate([0, 10, 0]) color(color_beak) rotate([90, 0, 0]) 
            cylinder(h=15, d1=4, d2=8);
    }

    // Eyes
    color(color_eyes) {
        translate([-4, 8, 28]) sphere(d=3);
        translate([4, 8, 28]) sphere(d=3);
    }

    // Antlers (deer-like)
    color(color_horns) {
        // Left antler
        translate([-6, 0, 38]) {
            rotate([0, 0, -20]) cylinder(h=12, d=2);
            translate([0, 0, 8]) rotate([0, 0, -45]) cylinder(h=8, d=1.5);
            translate([0, 0, 8]) rotate([0, 0, 45]) cylinder(h=6, d=1.5);
        }

        // Right antler
        translate([6, 0, 38]) {
            rotate([0, 0, 20]) cylinder(h=12, d=2);
            translate([0, 0, 8]) rotate([0, 0, 45]) cylinder(h=8, d=1.5);
            translate([0, 0, 8]) rotate([0, 0, -45]) cylinder(h=6, d=1.5);
        }
    }

    // Wings - bird-like with feather texture
    color(color_wings) {
        // Left wing
        translate([-15, -5, 10]) {
            rotate([0, 0, 20]) {
                // Main wing
                scale([1, 2, 0.5]) sphere(d=20);

                // Feathers
                for(i = [0:2]) {
                    translate([-5-i*2, -10-i*3, 0]) 
                        rotate([0, 0, -30-i*10]) 
                        scale([0.5, 1.2, 0.2]) 
                        sphere(d=8);
                }
            }
        }

        // Right wing
        translate([15, -5, 10]) {
            rotate([0, 0, -20]) {
                // Main wing
                scale([1, 2, 0.5]) sphere(d=20);

                // Feathers
                for(i = [0:2]) {
                    translate([5+i*2, -10-i*3, 0]) 
                        rotate([0, 0, 30+i*10]) 
                        scale([0.5, 1.2, 0.2]) 
                        sphere(d=8);
                }
            }
        }
    }

    // Legs
    color(color_body) {
        // Front legs
        for(side = [-1, 1]) {
            translate([side*8, -5, -10]) {
                cylinder(h=20, d=5);
                // Hooves
                translate([0, 0, -10]) color(color_horns) 
                    cylinder(h=3, d=6);
            }
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([side*6, 5, -10]) {
                cylinder(h=20, d=5);
                // Hooves
                translate([0, 0, -10]) color(color_horns) 
                    cylinder(h=3, d=6);
            }
        }
    }

    // Tail - bird-like with feathers
    color(color_wings) {
        translate([0, 15, -5]) {
            rotate([0, 0, 0]) {
                // Tail base
                cylinder(h=10, d=4);

                // Tail feathers
                for(i = [0:3]) {
                    translate([0, 8+i*2, 0]) 
                        rotate([0, 0, i*15-22.5]) 
                        scale([0.3, 1.5, 0.1]) 
                        sphere(d=6);
                }
            }
        }
    }

    // Magical glow spots (characteristic of perytons)
    color("#00FFFF") {
        for(pos = [[-10, 0, 5], [10, 0, 5], [0, -8, 8], [0, 8, 8]]) {
            translate(pos) sphere(d=1);
        }
    }
}

