// Qilin - Medium complexity realistic style
// Size: 80mm long, 50mm tall, 30mm wide

$fn = 32;

// Main color scheme
qilin_color = [0.8, 0.6, 0.4]; // Golden ochre
horn_color = [0.9, 0.9, 0.8]; // Ivory
scale_color = [0.6, 0.4, 0.3]; // Darker scales
mane_color = [0.9, 0.7, 0.5]; // Light mane
eye_color = [0.1, 0.1, 0.8]; // Blue eyes

module qilin() {
    // Body - deer-like torso
    color(qilin_color)
    union() {
        // Main body
        translate([0, 0, 15])
            scale([1.2, 0.8, 1])
            sphere(15);

        // Chest
        translate([-10, 0, 12])
            scale([1.5, 1, 0.8])
            sphere(12);

        // Hindquarters
        translate([15, 0, 10])
            scale([1.3, 1.1, 1])
            sphere(13);
    }

    // Neck
    color(qilin_color)
    translate([-5, 0, 30])
        rotate([0, -20, 0])
        cylinder(h=20, r1=8, r2=6);

    // Head - dragon/horse hybrid
    color(qilin_color)
    union() {
        // Main head
        translate([-15, 0, 35])
            scale([1.2, 0.8, 1])
            sphere(10);

        // Snout
        translate([-25, 0, 33])
            scale([1.5, 0.6, 0.5])
            sphere(6);
    }

    // Single horn (unicorn-like)
    color(horn_color)
    translate([-20, 0, 45])
        rotate([0, 20, 0])
        cylinder(h=15, r1=2, r2=0.5);

    // Mane - flowing hair
    color(mane_color)
    for(i = [-20:2:-5]) {
        translate([i, 0, 38])
            rotate([0, 90, sin(i*5)*10])
            cylinder(h=3, r=2);
    }

    // Scales along body
    color(scale_color)
    for(i = [-5:5:20]) {
        for(j = [-15:5:15]) {
            if(j*j + (i*0.5)*(i*0.5) < 225) {
                translate([i, j, 15 + sin(i*0.3)*3])
                    rotate([90, 0, 0])
                    cylinder(h=1, r1=3, r2=1);
            }
        }
    }

    // Legs - deer-like with hooves
    module leg(x, z, angle) {
        translate([x, 0, z])
            rotate([0, angle, 0])
            union() {
                // Upper leg
                color(qilin_color)
                cylinder(h=15, r=4);

                // Lower leg
                translate([0, 0, -15])
                    cylinder(h=15, r=3);

                // Hoof
                color(horn_color)
                translate([0, 0, -30])
                    scale([1, 1, 0.5])
                    sphere(3);
            }
    }

    // Front legs
    leg(-5, 15, 20);
    leg(-5, -15, 20);

    // Back legs
    leg(15, 12, -20);
    leg(15, -12, -20);

    // Tail - ox tail with tuft
    color(mane_color)
    union() {
        // Tail
        translate([25, 0, 15])
            rotate([0, -30, 0])
            cylinder(h=20, r1=3, r2=1);

        // Tuft
        translate([42, 0, 5])
            sphere(5);
    }

    // Eyes
    color(eye_color)
    translate([-22, 5, 35])
        sphere(1.5);
    translate([-22, -5, 35])
        sphere(1.5);

    // Small dragon wings (folded)
    color([0.8, 0.6, 0.4, 0.8])
    translate([5, 10, 30])
        rotate([0, -10, 20])
        scale([1, 0.3, 1])
        sphere(8);

    translate([5, -10, 30])
        rotate([0, -10, -20])
        scale([1, 0.3, 1])
        sphere(8);

    // Beard
    color(mane_color)
    translate([-23, 0, 30])
        for(i = [0:10:180]) {
            rotate([0, i, 0])
                translate([-2, sin(i)*3, 0])
                    cylinder(h=8, r=0.5);
        }
}

