// Shadhavar - Mythical unicorn-like creature with hollow horn that produces music
// Based on Persian mythology - spiral horn with hollow channels that creates melody

// Global parameters
scale_factor = 0.5;  // Scale to ~75mm length
body_length = 60 * scale_factor;
body_height = 35 * scale_factor;
leg_height = 25 * scale_factor;
horn_length = 40 * scale_factor;

// Main module
module shadhavar() {
    color("tan") {
        union() {
            // Main body
            translate([0, 0, leg_height]) 
                body();

            // Head and neck
            translate([body_length/2 - 5, 0, leg_height + body_height/2]) 
                head_and_neck();

            // Four legs
            for(i = [-1, 1]) {
                for(j = [-1, 1]) {
                    translate([i * body_length/4, j * body_height/3, 0])
                        leg();
                }
            }

            // Tail
            translate([-body_length/2, 0, leg_height + body_height/3])
                tail();
        }
    }
}

// Body module - horse-like torso
module body() {
    hull() {
        // Main torso section
        translate([0, 0, 0])
            scale([1.2, 0.8, 0.9])
            sphere(r=body_height/2.5);

        // Rear section
        translate([-body_length/3, 0, -body_height/8])
            scale([0.8, 0.7, 0.7])
            sphere(r=body_height/2.5);
    }
}

// Head and neck
module head_and_neck() {
    rotate([0, -15, 0]) {
        // Neck
        color("tan") {
            translate([-10 * scale_factor, 0, 0])
                rotate([0, 90, 0])
                cylinder(h=20 * scale_factor, r1=8 * scale_factor, r2=6 * scale_factor);
        }

        // Head
        translate([10 * scale_factor, 0, 5 * scale_factor])
            rotate([0, -20, 0])
            scale([1.3, 0.8, 0.9])
            sphere(r=8 * scale_factor);

        // Muzzle
        translate([20 * scale_factor, 0, 0])
            scale([1, 0.7, 0.6])
            sphere(r=6 * scale_factor);

        // Ears
        color("tan") {
            for(side = [-1, 1]) {
                translate([15 * scale_factor, side * 5 * scale_factor, 8 * scale_factor])
                    rotate([0, -20, side * 20])
                    cylinder(h=8 * scale_factor, r1=3 * scale_factor, r2=1 * scale_factor);
            }
        }

        // The magical horn - spiral with hollow channels
        color("ivory") {
            translate([25 * scale_factor, 0, 8 * scale_factor])
                rotate([0, 0, 0])
                spiral_horn();
        }
    }
}

// Spiral horn module
module spiral_horn() {
    // Main horn structure
    linear_extrude(height=horn_length, twist=720, scale=0.1)
        circle(r=3 * scale_factor);

    // Hollow channels along the horn - creates musical properties
    for(i = [0:3]) {
        rotate([0, 0, i * 90])
        translate([2 * scale_factor, 0, 0])
        cylinder(h=horn_length, r=0.5 * scale_factor);
    }
}

// Leg module
module leg() {
    color("tan") {
        // Upper leg
        translate([0, 0, leg_height/2])
            scale([0.8, 0.8, 1])
            cylinder(h=leg_height, r1=5 * scale_factor, r2=4 * scale_factor);

        // Lower leg
        translate([0, 0, leg_height/4])
            cylinder(h=leg_height/2, r=3 * scale_factor);

        // Hoof
        color("black")
        translate([0, 0, 1 * scale_factor])
            cylinder(h=4 * scale_factor, r1=3.5 * scale_factor, r2=3 * scale_factor);
    }
}

// Tail with flowing hair
module tail() {
    color("tan") {
        // Tail base
        translate([0, 0, 0])
            rotate([90, 0, 0])
            cylinder(h=15 * scale_factor, r1=4 * scale_factor, r2=2 * scale_factor);

        // Flowing tail strands
        for(i = [0:5]) {
            rotate([0, i * 15 - 30, 0])
            translate([-15 * scale_factor, 0, 0])
            scale([1, 0.3, 0.1])
            sphere(r=8 * scale_factor);
        }
    }
}

