color_bone = [0.8, 0.75, 0.65];
color_eyes = [0.9, 0.1, 0.1];
color_cloak = [0.05, 0.05, 0.05];

// Main module
module sluagh() {
    // Base positioning
    translate([0, 0, 10]) {
        // Cloaked body
        color(color_cloak) cloak();

        // Head with hood
        translate([0, 0, 35]) {
            color(color_dark) head();
        }

        // Multiple ghostly arms
        for(i = [-30, -15, 0, 15, 30]) {
            rotate([0, 0, i]) {
                translate([12, 0, 25]) {
                    color(color_cloak) arm();
                }
            }
        }

        // Floating skulls around the figure
        for(i = [0:45:315]) {
            rotate([0, 0, i]) {
                translate([20, 0, 20 + sin(i*2)*5]) {
                    color(color_bone) skull();
                }
            }
        }
    }
}

// Cloak module - tattered and flowing
module cloak() {
    difference() {
        // Main cloak body
        cylinder(h = 40, r1 = 18, r2 = 22, center = false);

        // Tattered bottom edge
        for(i = [0:30:330]) {
            rotate([0, 0, i]) {
                translate([15, 0, 0]) {
                    cube([10, 8, 15], center = true);
                }
            }
        }

        // Hollow center for lightness
        cylinder(h = 50, r1 = 14, r2 = 18, center = false);
    }
}

// Head module - shadowy with glowing eyes
module head() {
    union() {
        // Main head sphere
        sphere(r = 8);

        // Hood extension
        cylinder(h = 12, r1 = 10, r2 = 8);

        // Glowing eyes
        translate([-3, 6, 2]) {
            color(color_eyes) sphere(r = 1.5);
        }
        translate([3, 6, 2]) {
            color(color_eyes) sphere(r = 1.5);
        }

        // Shadow wisps
        for(i = [0:60:300]) {
            rotate([0, 0, i]) {
                translate([7, 0, -2]) {
                    scale([1, 0.3, 0.5]) sphere(r = 3);
                }
            }
        }
    }
}

// Arm module - skeletal hands emerging from cloak
module arm() {
    union() {
        // Cloak sleeve
        cylinder(h = 15, r1 = 3, r2 = 2);

        // Skeletal hand
        translate([0, 0, -8]) {
            // Palm
            sphere(r = 2.5);

            // Fingers - long and claw-like
            for(i = [0:72:288]) {
                rotate([0, 0, i]) {
                    translate([2, 0, 0]) {
                        cylinder(h = 6, r1 = 0.8, r2 = 0.3);
                    }
                }
            }
        }
    }
}

// Skull module - floating remnants
module skull() {
    scale([0.6, 0.6, 0.6]) {
        union() {
            // Main skull
            sphere(r = 5);

            // Jaw
            translate([0, -3, -2]) {
                cube([6, 4, 3], center = true);
            }

            // Eye sockets
            difference() {
                sphere(r = 5);
                translate([-1.5, 1.5, 1]) {
                    sphere(r = 1.2);
                }
                translate([1.5, 1.5, 1]) {
                    sphere(r = 1.2);
                }
            }

            // Cracks for aged appearance
            for(i = [0:45:135]) {
                rotate([0, 0, i]) {
                    translate([2, 0, 1]) {
                        cube([0.5, 3, 0.2], center = true);
                    }
                }
            }
        }
    }
}

