    color(color_bone) {
        for(i = [0:6]) {
            rotate([0, i * 15, 0]) {
                translate([0, 0, -i * 1.5]) 
                    scale([1.2, 1, 0.3]) 
                        sphere(4);
            }
        }
    }
}

