module alicanto() {
    scale(scale_factor) {
        // Main body
        color(gold) {
            translate([0, 0, 20]) {
                difference() {
                    sphere(15);  // Body sphere
                    translate([0, 0, -10]) cube([30, 30, 20], center=true);  // Flatten bottom
                }
            }
        }

        // Head
