            translate([32, 0, 30]) {
                rotate([0, -20, 0]) {
                    cylinder(h=12, r1=4, r2=2);
                }
            }
        }

        // Eyes (glowing emeralds)
        color(emerald) {
            translate([25, 6, 32]) sphere(3);
            translate([25, -6, 32]) sphere(3);
        }

        // Wings
        module wing(side) {
            translate([0, side * 15, 25]) {
                rotate([0, 0, side * 20]) {
                    color(gold) {
                        // Main wing structure
                        difference() {
                            scale([1, 2, 0.3]) sphere(12);
                            translate([0, -15, -5]) cube([25, 15, 10], center=true);
                        }
                    }

                    // Wing feathers
                    for (i = [0:3]) {
                        color(silver) {
                            translate([-5 + i*3, 10 + i*5, 0]) {
                                rotate([0, 0, -i * 10]) {
                                    scale([0.8, 1.5, 0.1]) sphere(5);
                                }
                            }
                        }
                    }
                }
            }
        }

        wing(1);  // Left wing
        wing(-1); // Right wing

        // Tail feathers like golden coins
        module tail_feather(offset) {
            translate([-20 - offset*3, 0, 25 - offset*2]) {
                rotate([0, 0, offset * 10]) {
                    color(gold) {
                        scale([0.2, 1, 1]) sphere(8);
                    }
                }
            }
        }

        for (i = [0:4]) {
            tail_feather(i);
        }

        // Legs
        module leg(side) {
            translate([5, side * 8, 10]) {
                color(bronze) {
                    cylinder(h=15, r=3);
                    translate([0, 0, -7.5]) {
                        scale([1.5, 3, 0.5]) sphere(4);  // Foot
                    }
                }
            }
