// Antaeus - Mythical Giant of Libya
// Realistic style, medium complexity

// Basic proportions
$fn = 50;
scale_factor = 0.4;
body_height = 60 * scale_factor;
shoulder_width = 35 * scale_factor;
head_size = 20 * scale_factor;

// Colors
color_body = "SaddleBrown";
color_skin = "Tan";
color_eyes = "Red";
color_earth = "DarkOliveGreen";

// Main module
module antaeus() {
    // Position base slightly above origin
    translate([0, 0, 5]) {
        // Body group
        union() {
            // Head with earth connection
            translate([0, 0, body_height + head_size/2]) {
                color(color_body) {
                    // Head sphere
                    sphere(head_size);

                    // Earth crown - connection to mother earth
                    difference() {
                        cylinder(head_size * 0.8, head_size * 1.2, head_size * 1.2);
                        translate([0, 0, -1]) cylinder(head_size * 0.8 + 2, head_size * 1.1, head_size * 1.1);
                    }

                    // Earth veins on head
                    for(i = [0:5]) {
                        rotate([0, 0, i * 60]) {
                            translate([head_size * 0.8, 0, head_size * 0.3]) 
                                rotate([90, 0, 0]) cylinder(head_size * 0.5, head_size * 0.1, 0);
                        }
                    }
                }

                // Eyes - glowing red
                color(color_eyes) {
                    translate([head_size * 0.4, head_size * 0.6, head_size * 0.2]) 
                        sphere(head_size * 0.15);
                    translate([head_size * 0.4, -head_size * 0.6, head_size * 0.2]) 
                        sphere(head_size * 0.15);
                }
            }

            // Neck
            translate([0, 0, body_height * 0.8]) {
                color(color_skin) cylinder(body_height * 0.2, head_size * 0.7, head_size * 0.9);
            }

            // Torso
            translate([0, 0, body_height/2]) {
                color(color_body) {
                    // Chest
                    scale([1, 0.8, 1.2]) sphere(shoulder_width/2);

                    // Abs
                    translate([0, 0, -body_height * 0.3]) {
                        scale([0.8, 0.6, 1]) sphere(shoulder_width/2);
                    }

                    // Earth cracks on torso
                    for(i = [0:3]) {
                        translate([0, -shoulder_width/2 + i * shoulder_width/3, body_height * 0.2]) {
                            rotate([0, 90, 0]) cylinder(shoulder_width/2, 1, 1);
                        }
                    }
                }
            }

            // Arms
            module arm(side) {
                translate([side * shoulder_width/2, 0, body_height * 0.7]) {
                    rotate([0, side * 20, 0]) {
                        // Upper arm
                        color(color_skin) cylinder(body_height * 0.3, shoulder_width * 0.15, shoulder_width * 0.12);

                        // Lower arm
                        translate([0, 0, body_height * 0.3]) {
                            rotate([side * 30, 0, 0]) {
                                color(color_skin) cylinder(body_height * 0.25, shoulder_width * 0.12, shoulder_width * 0.1);

                                // Giant fists
                                translate([0, 0, body_height * 0.25]) {
                                    color(color_body) sphere(shoulder_width * 0.2);
                                }
                            }
                        }
                    }
                }
            }

            arm(1);
            arm(-1);

            // Legs
            module leg(side) {
                translate([side * shoulder_width/3, 0, 0]) {
                    // Upper leg
                    color(color_skin) cylinder(body_height * 0.4, shoulder_width * 0.2, shoulder_width * 0.15);

                    // Lower leg
                    translate([0, 0, body_height * 0.4]) {
                        color(color_skin) cylinder(body_height * 0.35, shoulder_width * 0.15, shoulder_width * 0.12);

                        // Feet connected to earth
                        translate([0, 0, body_height * 0.35]) {
                            color(color_earth) {
                                scale([1.5, 2, 0.5]) sphere(shoulder_width * 0.15);

                                // Earth tendrils
                                for(i = [0:4]) {
                                    rotate([0, 0, i * 90 + 45]) {
                                        translate([shoulder_width * 0.2, 0, -5]) {
                                            cylinder(10, 2, 0.5);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }

            leg(1);
            leg(-1);
        }

        // Earth base - Antaeus is always connected to earth
        color(color_earth) {
            translate([0, 0, -5]) {
                cylinder(5, shoulder_width * 1.5, shoulder_width * 2);

                // Earth waves
                for(i = [0:8]) {
                    rotate([0, 0, i * 45]) {
                        translate([shoulder_width * 1.3, 0, 0]) {
                            scale([1, 0.3, 0.5]) sphere(shoulder_width * 0.3);
                        }
                    }
                }
            }
        }
    }
}

