// Aspidochelone - the giant sea-turtle-island from medieval bestiaries
// Overall length ~80 mm, printable without overhangs

scale_factor = 1;              // tune to fit your printer
shell_thickness = 1.2;         // 3 perimeters at 0.4 mm nozzle
base_height = 2;

// Main body: a low, wide turtle shell merging into an island
module body() {
    difference() {
        // broad oval shell
        scale([1.3, 1, 0.5])
            sphere(d = 50 * scale_factor, $fn = 64);

        // hollow underside so it prints as a shell rather than solid
        translate([0, 0, -shell_thickness])
            scale([1.25, 0.95, 0.45])
                sphere(d = 48 * scale_factor, $fn = 64);
    }
}

// Scutes on the back  arranged in 5 longitudinal rows
module scutes() {
    row_spacing = 8 * scale_factor;
    for (row = [-2 : 2]) {
        scute_count = 6 - abs(row);          // fewer scutes toward edges
        for (i = [0 : scute_count - 1]) {
            y = (i - scute_count/2 + 0.5) * row_spacing * 1.2;
            x = row * row_spacing * 1.1;
            translate([x, y, 0])
                scale([1.3, 1, 0.6])
                    rotate([90, 0, 0])
                        cylinder(h = 3 * scale_factor, d1 = 6 * scale_factor, d2 = 5 * scale_factor, $fn = 6);
        }
    }
}

// Head protruding from front
module head() {
    translate([30 * scale_factor, 0, -5 * scale_factor])
        rotate([0, -10, 0])
            union() {
                // snout
                color("DarkOliveGreen")
                    scale([1.2, 0.7, 0.7])
                        sphere(d = 12 * scale_factor, $fn = 32);

                // eyes
                for (side = [-1, 1]) {
                    translate([6 * scale_factor, side * 4 * scale_factor, 3 * scale_factor])
                        color("black")
                            sphere(d = 2 * scale_factor, $fn = 16);
                }
            }
}

// Flippers  thick and rounded to print without supports
module flipper(angle) {
    rotate([0, 0, angle])
        translate([0, 20 * scale_factor, -8 * scale_factor])
            rotate([20, 0, 0])
                color("SeaGreen")
                    difference() {
                        scale([2.2, 1, 0.4])
                            sphere(d = 18 * scale_factor, $fn = 32);
                        translate([0, 0, 1])
                            scale([1.9, 0.8, 0.3])
                                sphere(d = 18 * scale_factor, $fn = 32);
                    }
}

// Tiny trees and grass growing on the island shell
module vegetation() {
    for (i = [0 : 7]) {
        a = i * 137.5;          // golden angle for even spread
        r = 15 * (1 + sin(i * 30)) * scale_factor;
        translate([r * cos(a), r * sin(a), 2 * scale_factor])
            color("ForestGreen")
                cylinder(h = 5 * scale_factor, d = 1.5 * scale_factor, $fn = 8);
    }
}

// Optional rocky outcrop / miniature cliff
module rock_spire() {
    translate([-15 * scale_factor, 0, 0])
        color("SlateGray")
            for (i = [0 : 3]) {
                rotate([0, 0, i * 90])
                    translate([i * 1.5, 0, i * 1.5])
                        cylinder(h = 8 * scale_factor, d1 = 4 * scale_factor, d2 = 2 * scale_factor, $fn = 6);
            }
}

// Assembly
union() {
    // main body
    color("DarkKhaki") body();

    // external details
    color("OliveDrab") scutes();
    head();

    // four flippers
    for (a = [-45, 45]) {
        flipper(a);
        mirror([0, 1, 0]) flipper(a);
    }

    // island vegetation
    vegetation();
    rock_spire();

    // shallow base to ensure bed adhesion
    translate([0, 0, -base_height])
        color("LightBlue")
            cylinder(h = base_height, d = 60 * scale_factor, $fn = 64);
}

