                cylinder(h=15, d1=12, d2=8);
                cylinder(h=15, d1=12-wall*2, d2=8-wall*2);
            }
        }

        // Tail
        translate([0, -12, -2]) rotate([0, 10, 0]) {
            difference() {
                cylinder(h=25, d1=12, d2=6);
                cylinder(h=25, d1=12-wall*2, d2=6-wall*2);
            }
        }
    }
}

// Head module
module dragon_head() {
    color("darkslategray") {
        translate([0, 25, 8]) rotate([0, -30, 0]) {
            difference() {
                // Main head shape
                scale([0.8, 1.2, 0.9]) sphere(d=15);
                scale([0.7, 1.1, 0.8]) sphere(d=15);

                // Eye sockets
                translate([4, 3, 2]) sphere(d=3);
                translate([-4, 3, 2]) sphere(d=3);
            }

            // Snout
            translate([0, 7, -2]) {
                difference() {
                    scale([0.8, 1.5, 0.6]) sphere(d=10);
                    scale([0.7, 1.4, 0.5]) sphere(d=10);
                }
            }

            // Horns
            color("dimgray") {
                translate([3, -2, 8]) rotate([0, -20, 20]) 
                    cylinder(h=12, d1=4, d2=2);
                translate([-3, -2, 8]) rotate([0, -20, -20]) 
                    cylinder(h=12, d1=4, d2=2);
            }
        }
    }
}

// Wing module
module wing(side) {
    // side: 1 for right, -1 for left
    color("darkslategray", 0.7) {
        translate([side * 15, 0, 5]) rotate([0, 0, side * -10]) {
            // Main wing membrane
            difference() {
                // Outer wing shape
                linear_extrude(height=wall) {
                    polygon(points=[
                        [0, 0],
                        [side * 30, -5],
                        [side * 35, 10],
                        [side * 25, 25],
                        [side * 10, 20],
                        [0, 5]
                    ]);
                }

                // Inner cutouts for hollow effect
                for(i=[1:3]) {
                    translate([side * (10 + i*6), 10, -1]) 
                        scale([0.8, 0.8, 1]) 
                        circle(d=8);
                }
            }

            // Wing bones
            color("dimgray") {
                for(i=[0:2]) {
                    rotate([0, 0, side * (20 + i*15)]) {
                        translate([0, 0, wall/2]) 
                            cube([20 + i*5, wall, wall]);
                    }
                }
            }
        }
    }
}

// Legs module
module legs() {
    color("darkslategray") {
        for(side=[-1, 1]) {
            // Front legs
            translate([side * 8, 8, -5]) {
                difference() {
                    cylinder(h=12, d=5);
                    cylinder(h=12, d=5-wall*2);
                }
                translate([0, 0, -6]) {
                    difference() {
                        sphere(d=6);
                        sphere(d=6-wall*2);
                    }
                }
            }

            // Back legs
            translate([side * 6, -8, -5]) {
                difference() {
                    cylinder(h=15, d=6);
                    cylinder(h=15, d=6-wall*2);
                }
                translate([0, 0, -7.5]) {
                    difference() {
                        sphere(d=7);
                        sphere(d=7-wall*2);
                    }
                }
            }
        }
    }
}

// Glowing accents
module glowing_accents() {
    color("darkviolet", 0.8) {
        // Eye glow
        translate([4, 28, 10]) sphere(d=2);
        translate([-4, 28, 10]) sphere(d=2);

        // Wing edge glow
        for(side=[-1, 1]) {
            translate([side * 30, 10, 5 + wall/2]) 
                cylinder(h=wall, d=3);
        }

        // Tail tip glow
        translate([0, -35, -5]) sphere(d=3);
    }
}

// Assembly
union() {
    dragon_body();
    dragon_head();
    wing(1);  // Right wing
    wing(-1); // Left wing
    legs();
    glowing_accents();
}

// Icesylph - A mythical ice elemental creature
// Realistic style, medium complexity

// Colors
color_ice = [0.9, 0.95, 1.0];
color_frost = [0.8, 0.9, 1.0];
color_crystal = [0.7, 0.9, 1.0];

// Main module
module icesylph() {
    union() {
        // Body
        color(color_ice)
        translate([0, 0, 5])
        scale([1, 0.6, 1.5])
        sphere(8, $fn=32);

        // Head
        color(color_frost)
        translate([0, 0, 20])
        scale([0.8, 0.7, 1])
        sphere(6, $fn=32);

        // Snout
        color(color_frost)
        translate([0, 7, 19])
        scale([0.6, 0.8, 0.4])
        rotate([20, 0, 0])
        cylinder(h=5, r=2, $fn=16);

        // Eyes (glowing crystal orbs)
        for(side = [-1, 1]) {
            color(color_crystal)
            translate([side * 2.5, 5, 22])
            scale([0.8, 0.8, 0.5])
            sphere(1.5, $fn=16);
        }

        // Horns (ice crystal formations)
        for(side = [-1, 1]) {
            color(color_crystal)
            translate([side * 3, 3, 25])
            rotate([0, -20, side * 30])
            cylinder(h=8, r1=1.5, r2=0.5, $fn=8);

            // Horn branches
            translate([side * 3.5, 3.5, 28])
            rotate([20, 0, side * 45])
            cylinder(h=4, r1=1, r2=0.3, $fn=8);
        }

        // Wings (membranous ice formations)
        wing_module(1);
        wing_module(-1);

        // Tail (segmented ice tail)
        color(color_frost)
        for(i = [0:4]) {
            translate([0, -6 - i * 3, 5 - i * 1.5])
            scale([1 - i * 0.15, 1 - i * 0.15, 1])
            rotate([0, 0, sin(i * 20) * 15])
            sphere(4 - i * 0.5, $fn=24);
        }

        // Frost patterns on body
        frost_patterns();

        // Legs
        leg_module(1);
        leg_module(-1);
    }
}

// Wing module
module wing_module(side) {
    color(color_ice)
    translate([side * 8, 0, 15])
    rotate([0, 0, side * 20])
    rotate([0, -30, 0])
    scale([side, 1, 1])

    difference() {
        union() {
            // Main wing membrane
            translate([0, 0, 0])
            scale([1, 0.8, 0.1])
            sphere([15, 12, 1], $fn=32);

            // Wing fingers (ice spars)
            for(i = [0:3]) {
                translate([i * 4, 0, 0])
                rotate([0, 0, -i * 20])
                cylinder(h=12 - i * 2, r=0.8, $fn=8);
            }
        }

        // Cut wing shape
        translate([-5, -8, -2])
        cube([25, 20, 4]);
    }
}

// Leg module
module leg_module(side) {
    color(color_frost)
    translate([side * 4, 0, 0])

    union() {
        // Upper leg
        translate([side * 2, -2, 2])
        rotate([0, side * 15, 0])
        cylinder(h=6, r=1.5, $fn=16);

        // Lower leg
        translate([side * 3, -4, -1])
        rotate([0, side * 30, 0])
        cylinder(h=5, r=1.2, $fn=16);

        // Paw
        translate([side * 3.5, -5, -4])
        sphere(1.8, $fn=16);

        // Toes
        for(toe = [-1, 0, 1]) {
            translate([side * 3.5 + toe * 0.8, -6, -4])
            rotate([0, toe * 10, 0])
            cylinder(h=2, r=0.5, $fn=8);
        }
    }
}

// Frost patterns module
module frost_patterns() {
    color(color_crystal)
    for(i = [0:11]) {
        angle = i * 30;
        radius = 7 + sin(i * 45) * 1.5;

        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            5 + sin(i * 25) * 3
        ])
        scale([0.5, 0.5, 0.1])
        sphere(1, $fn=6);
    }
}

