                                scale([0.8-i*0.1, 0.3, 1]) 
                                sphere(d=8-i*2, $fn=8);
                            }
                        }
                    }

                    // Wing bones
                    color(kindle_red) {
                        for(i = [0:2]) {
                            rotate([0, 0, i*30]) {
                                translate([0, -i*2, 0]) {
                                    cylinder(h=10, d1=0.8, d2=0.3);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Leg module
module kindlekin_leg(side=1, front=1) {
    color(kindle_orange) {
        translate([side*6, front*10, 0]) {
            // Upper leg
            rotate([0, 20*side, 0]) {
                cylinder(h=8, d1=4, d2=3, $fn=8);

                // Lower leg
                translate([0, 0, -8]) {
                    rotate([30, 0, 0]) {
                        cylinder(h=6, d1=3, d2=2, $fn=8);

                        // Foot
                        translate([0, 0, -6]) {
                            rotate([0, 0, 45*side]) {
                                scale([2, 0.8, 0.5]) 
                                sphere(d=3, $fn=6);

                                // Toes
                                for(i = [-1:1]) {
                                    translate([i*0.8, 1.5*side, 0]) {
                                        cylinder(h=1.5, d=0.8, $fn=6);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Fire breath effect
module kindlekin_fire_breath() {
    color(kindle_yellow) {
        translate([0, 28, 8]) {
            rotate([0, -30, 0]) {
                for(i = [1:5]) {
                    translate([0, i*2, 0]) {
                        scale([1-i*0.15, 1-i*0.15, 1-i*0.15]) {
                            sphere(d=3, $fn=8);
                        }
                    }
                }
            }
        }
    }
}

// Scale texture module
module scale_texture(base_pos=[0,0,0], radius=8, count=12) {
    color(kindle_red) {
        for(i = [0:count-1]) {
            angle = i * 360 / count;
            translate([
                base_pos[0] + cos(angle) * radius * 0.8,
                base_pos[1] + sin(angle) * radius * 0.8,
                base_pos[2]
            ]) {
                rotate([0, 0, angle]) {
                    scale([0.3, 0.5, 0.1]) 
                    sphere(d=2, $fn=5);
                }
            }
        }
    }
}

// Assemble the complete Kindlekin
module kindlekin() {
    scale([1.2, 1.2, 1.2]) {
        kindlekin_body();
        kindlekin_head();
        kindlekin_horns();
        kindlekin_tail();

        // Wings
        kindlekin_wing(1);
        kindlekin_wing(-1);

        // Legs
        kindlekin_leg(1, 1);   // Front right
        kindlekin_leg(-1, 1);  // Front left
        kindlekin_leg(1, -1);  // Back right
        kindlekin_leg(-1, -1); // Back left

        // Fire breath
        kindlekin_fire_breath();

        // Scale texture on body
        scale_texture([0, 0, 6], 6, 8);
        scale_texture([0, 0, 3], 7, 10);
        scale_texture([0, 0, 0], 6, 8);
    }
}

