// Lavaspark - A mythical fire elemental creature
// 75mm tall, designed for 3D printing with minimal overhangs

// Main body parameters
body_height = 30;
body_width = 25;
body_depth = 20;

// Head parameters
head_radius = 12;

// Wing parameters
wing_length = 35;
wing_width = 20;

// Tail segments
tail_segments = 5;
tail_length = 40;

// Colors
lava_color = [0.8, 0.2, 0];      // Deep red-orange
ember_color = [1, 0.4, 0];      // Bright orange
core_color = [1, 0.8, 0];       // Yellow-white hot core

module lavaspark() {
    union() {
        // Main molten body
        color(lava_color)
        translate([0, 0, 15])
        scale([1, 0.8, 1.2])
        sphere(d=body_height);

        // Inner core glow
        color(core_color)
        translate([0, 0, 15])
        scale([0.7, 0.6, 0.9])
        sphere(d=body_height * 0.8);

        // Head with horns
        translate([0, body_width/2 + 5, body_height - 5]) {
            // Main head sphere
            color(lava_color)
            sphere(d=head_radius * 2);

            // Inner head glow
            color(ember_color)
            scale([0.8, 0.8, 0.8])
            sphere(d=head_radius * 2);

            // Horns - two curved back
            for(i = [-1, 1]) {
                translate([i * 8, 0, 8]) {
                    rotate([0, i * 15, 0])
                    color(lava_color)
                    cylinder(h=15, r1=3, r2=1);
                }
            }

            // Eyes
            for(i = [-1, 1]) {
                translate([i * 5, head_radius - 2, 3]) {
                    color(core_color)
                    sphere(d=3);
                }
            }
        }

        // Wings - flame-shaped
        for(side = [-1, 1]) {
            translate([side * (body_width/2 + 5), 0, body_height]) {
                rotate([0, side * 30, 0])
                color(ember_color)
                linear_extrude(height=2)
                polygon(points=[
                    [0, 0],
                    [wing_length * 0.3, wing_width * 0.8],
                    [wing_length * 0.7, wing_width * 0.6],
                    [wing_length, wing_width * 0.3],
                    [wing_length * 0.8, 0],
                    [wing_length, -wing_width * 0.3],
                    [wing_length * 0.7, -wing_width * 0.6],
                    [wing_length * 0.3, -wing_width * 0.8],
                    [0, 0]
                ]);
            }
        }

        // Tail - segmented flame
        color(lava_color)
        for(i = [0:tail_segments-1]) {
            t = i / (tail_segments - 1);
            translate([0, -body_depth/2 - tail_length * t, 
                      body_height * 0.5 - tail_length * t * 0.3]) {
                scale([1 - t * 0.5, 1 - t * 0.5, 1 - t * 0.5])
                rotate([0, 0, -t * 45])
                sphere(d=8);
            }
        }

        // Legs - simple pillars of fire
        for(x = [-1, 1]) {
            for(y = [-1, 1]) {
                translate([x * body_width/3, y * body_depth/3, 0]) {
                    color(ember_color)
                    cylinder(h=15, r1=4, r2=2);
                }
            }
        }

        // Back spikes
        for(i = [0:4]) {
            x = (i - 2) * 5;
            translate([x, 0, body_height + 5]) {
                color(lava_color)
                rotate([15, 0, 0])
                cylinder(h=8, r1=2, r2=1);
            }
        }

        // Ground crackle effect base
        translate([0, 0, -2]) {
            color(lava_color)
            scale([1.5, 1.2, 0.5])
            cylinder(h=4, r=body_width/2);

            // Crack details
            for(i = [0:5]) {
                angle = i * 60;
                rotate([0, 0, angle])
                translate([body_width/2 + 5, 0, 0])
                scale([0.2, 0.5, 1])
                cube([15, 2, 4]);
            }
        }
    }
}

// Render the lavaspark
lavaspark();

