// Mossveil - A mythical forest spirit covered in living moss with translucent wings
// Realistic style, medium complexity

// Parameters for easy scaling
base_scale = 0.8;
body_height = 60 * base_scale;
body_width = 35 * base_scale;
wing_span = 80 * base_scale;

// Main creature assembly
module mossveil_creature() {
    union() {
        // Main body - bulbous moss-covered form
        color("forestgreen") 
        translate([0, 0, 15])
        scale([1, 0.8, 1.2])
        sphere(d=body_width);

        // Lower body extension
        color("darkgreen")
        translate([0, 0, 0])
        cylinder(h=25 * base_scale, d1=body_width * 0.9, d2=body_width * 0.6);

        // Head with moss texture
        color("forestgreen")
        translate([0, -15 * base_scale, body_height])
        scale([1.1, 1.3, 0.9])
        sphere(d=body_width * 0.7);

        // Eyes - glowing amber orbs
        color("goldenrod")
        for(x = [-8, 8]) {
            translate([x * base_scale, -22 * base_scale, body_height + 5 * base_scale])
            sphere(d=6 * base_scale);
        }

        // Branch-like horns with moss
        color("saddlebrown")
        for(angle = [30, -30]) {
            rotate([0, 0, angle])
            translate([0, -25 * base_scale, body_height + 8 * base_scale])
            rotate([angle * 0.7, 0, 0])
            cylinder(h=20 * base_scale, d1=4 * base_scale, d2=2 * base_scale);

            color("forestgreen")
            translate([sin(angle) * 8 * base_scale, -25 * base_scale, body_height + 15 * base_scale])
            sphere(d=5 * base_scale);
        }

        // Translucent wings with vein structure
        color("aquamarine", 0.4)
        for(side = [-1, 1]) {
            translate([side * 15 * base_scale, 0, body_height - 10 * base_scale])
            rotate([0, side * 20, 0])
            wing(side);
        }

        // Root-like legs
        color("saddlebrown")
        for(x = [-12, 0, 12]) {
            translate([x * base_scale, 0, 0])
            cylinder(h=15 * base_scale, d1=6 * base_scale, d2=4 * base_scale);
        }

        // Moss clusters on body
        color("limegreen")
        for(i = [1:8]) {
            angle = i * 45;
            radius = body_width * 0.4;
            translate([cos(angle) * radius, -sin(angle) * radius * 0.8, 20 + i * 3])
            sphere(d=8 * base_scale);
        }

        // Magical spore particles
        color("palegreen")
        for(i = [1:20]) {
            translate([
                (rands(-25, 25, 1)[0]) * base_scale,
                (rands(-20, 20, 1)[0]) * base_scale,
                (rands(10, 50, 1)[0]) * base_scale
            ])
            sphere(d=2 * base_scale);
        }
    }
}

// Wing module with membrane structure
module wing(side) {
    difference() {
        // Main wing membrane
        union() {
            translate([0, 0, 0])
            scale([1, 1.5, 0.1])
            sphere(d=wing_span * 0.6);

            // Wing extension
            translate([side * 10 * base_scale, -20 * base_scale, 5 * base_scale])
            rotate([0, 0, side * 15])
            scale([0.8, 1.2, 0.1])
            sphere(d=wing_span * 0.4);
        }

        // Vein cutouts
        for(i = [1:3]) {
            rotate([0, 0, side * i * 20])
            translate([side * 15 * base_scale, -i * 8 * base_scale, 0])
            scale([0.05, 1.2, 0.2])
            cube([wing_span, wing_span * 0.6, 10]);
        }

        // Wing edge detail
        translate([side * 25 * base_scale, -35 * base_scale, 0])
        scale([0.3, 0.3, 0.1])
        sphere(d=wing_span * 0.3);
    }
}

