// Nettlethorn - A mythical plant-creature hybrid with venomous spines and bioluminescent features
// Designed for 3D printing - no overhangs beyond 45 degrees

$fn = 32; // Smooth curves for realistic appearance

// Main colors
color_plant = [0.2, 0.4, 0.1]; // Dark forest green
color_spine = [0.3, 0.2, 0.1]; // Brown woody spines
color_glow = [0.1, 0.8, 0.3]; // Bioluminescent green
color_flower = [0.7, 0.1, 0.5]; // Purple bioluminescent flowers

// Overall scaling factor (100mm height)
scale_factor = 1;

module main_body() {
    color(color_plant)
    union() {
        // Central stalk (main trunk)
        cylinder(h = 80 * scale_factor, r1 = 12 * scale_factor, r2 = 8 * scale_factor, center = false);

        // Bulbous root base
        translate([0, 0, -5 * scale_factor])
            sphere(r = 15 * scale_factor);
    }
}

module thorn_spine(pos, angle, length) {
    color(color_spine)
    translate(pos)
    rotate([90 + angle, 0, 0])
    cylinder(h = length, r1 = 1.5 * scale_factor, r2 = 0.5 * scale_factor, center = false);
}

module thorn_cluster(height, count) {
    for(i = [0:count-1]) {
        angle = i * 360 / count;
        radius = 10 * scale_factor + height * 0.1;
        x_pos = cos(angle) * radius;
        y_pos = sin(angle) * radius;

        // Alternating spine lengths for natural look
        spine_len = 8 * scale_factor + (i % 2) * 3 * scale_factor;

        thorn_spine([x_pos, y_pos, height], 20 + (i % 3) * 5, spine_len);
    }
}

module bioluminescent_node(pos) {
    color(color_glow)
    translate(pos)
    sphere(r = 3 * scale_factor);

    // Inner brighter core
    color([color_glow[0] + 0.2, color_glow[1] + 0.1, color_glow[2] + 0.1])
    translate(pos)
    sphere(r = 1.5 * scale_factor);
}

module flower_cluster(pos) {
    color(color_flower)
    translate(pos)
    union() {
        // Central flower
        sphere(r = 4 * scale_factor);

        // Petals around the flower
        for(i = [0:5]) {
            rotate([0, 0, i * 60])
            translate([3 * scale_factor, 0, 0])
            scale([1, 0.5, 0.3])
            sphere(r = 2 * scale_factor);
        }
    }
}

module leaf_segment(height, angle) {
    color(color_plant)
    rotate([0, 0, angle])
    translate([8 * scale_factor, 0, height])
    rotate([0, 30, 0])
    scale([1, 0.3, 0.05])
    sphere(r = 12 * scale_factor);
}

module vine_tendril(start_height, direction) {
    color(color_plant)
    translate([0, 0, start_height])
    rotate([0, 0, direction * 90])
    for(i = [0:3]) {
        translate([i * 5 * scale_factor, sin(i * 30) * 3 * scale_factor, 0])
        rotate([0, 0, i * 20])
        cylinder(h = 6 * scale_factor, r = 2 * scale_factor, center = true);
    }
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor])
union() {
    // Base structure
    main_body();

    // Thorns in spiral pattern up the stalk
    for(level = [10:15:70]) {
        thorn_cluster(level, 8 + level / 10);
    }

    // Bioluminescent nodes
    bioluminescent_node([0, 0, 75]);
    bioluminescent_node([8, 8, 60]);
    bioluminescent_node([-8, -8, 45]);

    // Flower clusters at top
    flower_cluster([0, 0, 80]);
    flower_cluster([6, 6, 78]);
    flower_cluster([-6, -6, 78]);

    // Leaf segments
    for(i = [0:3]) {
        height = 25 + i * 15;
        leaf_segment(height, i * 90);
        leaf_segment(height + 5, i * 90 + 45);
    }

    // Vine tendrils
    vine_tendril(30, 1);
    vine_tendril(40, -1);
    vine_tendril(50, 0.5);

    // Additional small thorns for detail
    for(i = [0:15]) {
        angle = i * 24;
        height = 5 + i * 5;
        radius = 9 + sin(i * 3) * 2;
        x = cos(angle) * radius;
        y = sin(angle) * radius;
        thorn_spine([x, y, height], 15, 4);
    }
}

