// Bark texture module for realistic tree-like skin
module bark_texture(size, segments=8) {
    difference() {
        cube(size, center=true);
        for(i=[0:segments-1]) {
            rotate([0, 0, i*360/segments])
            translate([0, size[1]*0.4, 0])
            cylinder(h=size[2]*1.2, r=size[0]*0.1, center=true);
        }
    }
