    color("darkgreen") translate([0, 0, -radius*0.5]) sphere(r=radius*1.2);
}

// Horn module with spiral texture
module spiral_horn(height, base_radius) {
    union() {
        // Main horn
        cylinder(h=height, r1=base_radius, r2=base_radius*0.3);
        // Spiral ridges
        for(i=[0:5]) {
            rotate([0, 0, i*60])
            translate([base_radius*0.7, 0, 0])
            cylinder(h=height, r=base_radius*0.2);
