// Juniperling - A mythical tree-spirit creature with juniper-like features
// Realistic style, medium complexity

$fn = 32;

// Main color scheme
juniper_green = [0.2, 0.4, 0.2];
juniper_blue = [0.1, 0.2, 0.3];
wood_brown = [0.4, 0.3, 0.2];
berry_purple = [0.3, 0.1, 0.4];

// Main body module
module juniperling_body() {
    union() {
        // Torso - tapered cylinder
        color(juniper_green)
        cylinder(h = 30, r1 = 12, r2 = 8, center = true);

        // Head
        translate([0, 0, 25])
        color(juniper_green)
        sphere(r = 8);

        // Bark texture rings
        for(i = [1:5]) {
            translate([0, 0, -15 + i*6])
            color(wood_brown)
            rotate_extrude()
            translate([11, 0, 0])
            circle(r = 0.5);
        }
    }
}

// Arms made of twisted branches
module arm(side) {
    translate([side * 15, 0, 15])
    rotate([0, 0, side * 20])
    union() {
        // Upper arm
        color(wood_brown)
        cylinder(h = 15, r = 2, center = true);

        // Lower arm
        translate([0, 0, 8])
        rotate([0, side * 30, 0])
        color(wood_brown)
        cylinder(h = 12, r = 1.5, center = true);

        // Hand with three fingers
        translate([0, 0, 14])
        for(f = [-1:1]) {
            translate([f * 2, 0, 0])
            rotate([0, f * 15, 0])
            color(wood_brown)
            cylinder(h = 4, r = 0.8, center = true);
        }
    }
}

// Legs with root-like feet
module leg(side) {
    translate([side * 8, 0, -20])
    rotate([0, 0, side * 10])
    union() {
        // Upper leg
        color(wood_brown)
        cylinder(h = 18, r = 3, center = true);

        // Lower leg
        translate([0, 0, -9])
        color(wood_brown)
        cylinder(h = 15, r = 2.5, center = true);

        // Root-like foot
        translate([0, 0, -18])
        difference() {
            color(wood_brown)
            sphere(r = 4);
            // Carve out bottom for flat printing
            translate([0, 0, -4])
            cube([10, 10, 8], center = true);
        }

        // Root tendrils
        for(a = [0:60:300]) {
            rotate([0, 0, a])
            translate([3, 0, -20])
            color(wood_brown)
            cylinder(h = 6, r1 = 1, r2 = 0.3, center = true);
        }
    }
}

// Juniper-like wings made of scales
module wing(side) {
    translate([side * 15, 0, 20])
    rotate([0, side * -20, side * 30])
    union() {
        // Wing base
        color(juniper_blue)
        cylinder(h = 3, r = 2, center = true);

        // Wing membrane segments
        for(i = [0:3]) {
            rotate([0, 0, i * 15 * side])
            translate([5 + i*3, 0, 0])
            scale([1, 0.3, 1])
            color(juniper_blue)
            sphere(r = 4 - i);

            // Scales on wings
            for(j = [0:5]) {
                rotate([0, 0, j * 25 * side])
                translate([3 + i*2, 0, 0])
                color(juniper_green)
                scale([1, 0.5, 0.2])
                sphere(r = 1);
            }
        }
    }
}

// Antler-like juniper branches
module antler(side) {
    translate([side * 6, 0, 30])
    rotate([0, 0, side * 45])
    union() {
        // Main branch
        color(wood_brown)
        cylinder(h = 12, r = 1.5, center = true);

        // Side branches
        for(i = [1:3]) {
            translate([0, 0, 4 + i*2])
            rotate([0, 0, i * 30 * side])
            color(wood_brown)
            cylinder(h = 6, r = 0.8, center = true);

            // Juniper berries
            translate([3, 0, 0])
            color(berry_purple)
            sphere(r = 0.8);
        }

        // Tip berry cluster
        translate([0, 0, 7])
        for(b = [0:90:270]) {
            rotate([b, 0, 0])
            translate([0, 0, 1.5])
            color(berry_purple)
            sphere(r = 0.6);
        }
    }
}

// Magical aura particles
module aura_particle(x, y, z) {
    translate([x, y, z])
    color([0.5, 0.7, 0.5, 0.5])
    sphere(r = 0.5);
}

// Main assembly
union() {
    // Body
    juniperling_body();

    // Arms
    arm(1);
    arm(-1);

    // Legs
    leg(1);
    leg(-1);

    // Wings
    wing(1);
    wing(-1);

    // Antlers
    antler(1);
    antler(-1);

    // Magical aura
    for(i = [0:20]) {
        aura_particle(
            sin(i*137.5)*15,
            cos(i*137.5)*15,
            10 + sin(i*73.2)*5
        );
    }
}

