    module root_spread(count) {
        for(i = [0:count-1]) {
            rotate([0, 0, i*360/count])
            translate([10, 0, -5])
            rotate([-90, 0, 0])
            color([0.25, 0.15, 0.1])
            cylinder(h=20, d1=5, d2=2, $fn=8);
