            color(glow_color) {
                for(side = [-1, 1]) {
                    translate([head_radius * 0.7, side * head_radius * 0.6, head_radius * 0.2]) {
                        difference() {
                            sphere(r=2);
                            sphere(r=1.5);
                        }
                    }
                }
            }
        }

        // Wings
        for(side = [-1, 1]) {
            translate([body_length/4, side * body_width/2, body_height/3]) {
                rotate([0, 0, side * 20]) {
                    wing(side);
                }
            }
        }

        // Tail segments
        translate([-body_length/2, 0, -body_height/4]) {
            tail();
        }

        // Legs
        for(side = [-1, 1]) {
            translate([body_length/4, side * body_width/2, -body_height/2]) {
                leg();
            }
        }

        // Luminous spine ridges
        color(glow_color) {
            for(pos = [-20:10:20]) {
                translate([pos, 0, body_height/2 - 2]) {
                    rotate([90, 0, 0]) {
                        difference() {
                            cylinder(h=5, r1=3, r2=1);
                            cylinder(h=5, r1=2, r2=0);
                        }
                    }
                }
            }
        }
    }
}

// Wing module with membrane structure
module wing(side) {
    color(body_color) {
        // Main wing arm
        translate([0, 0, 0]) {
            rotate([0, 90, 0]) {
                difference() {
                    cylinder(h=wing_depth/2, r=2);
                    cylinder(h=wing_depth/2, r=1);
                }
            }
        }

        // Wing membrane - hollow framework
        for(i = [0:30:120]) {
            rotate([0, 0, i * side]) {
                translate([wing_depth/2, 0, 0]) {
                    scale([1, 0.1, 0.8]) {
                        difference() {
                            sphere(r=wing_span/3 - i/3);
                            sphere(r=wing_span/3 - i/3 - wall_thickness);
                        }
                    }
                }
            }
        }

        // Wing struts
        for(strut = [30, 60, 90]) {
            rotate([0, 0, strut * side]) {
                translate([wing_depth/4, 0, 0]) {
                    rotate([0, 90, 0]) {
                        difference() {
                            cylinder(h=wing_span/3 - strut/3, r=1);
                            cylinder(h=wing_span/3 - strut/3, r=0.5);
                        }
                    }
                }
            }
        }
    }
}

// Tail module with segmented structure
module tail() {
    color(body_color) {
        for(i = [0:tail_segments-1]) {
            translate([-i * tail_length/tail_segments, 0, 0]) {
                rotate([0, 0, sin(i * 15) * 10]) {
                    scale([1 - i * 0.08, 1 - i * 0.08, 1 - i * 0.08]) {
                        difference() {
                            sphere(r=body_width/3 - i);
                            sphere(r=body_width/3 - i - wall_thickness);
                        }
                    }
                }
            }
        }
    }
}

// Horn module with spiral texture
module horn() {
    color(accent_color) {
        difference() {
            cylinder(h=horn_length, r1=2, r2=0.5);
            cylinder(h=horn_length, r1=1.5, r2=0);
        }

        // Spiral ridges
        for(i = [0:30:360]) {
            rotate([0, 0, i]) {
                translate([1.8, 0, i/360 * horn_length]) {
                    difference() {
                        cube([0.4, 0.2, horn_length/12]);
                        cube([0.3, 0.1, horn_length/12]);
                    }
                }
            }
        }
    }
}

// Leg module
module leg() {
    color(body_color) {
        // Upper leg
        difference() {
            cylinder(h=body_height/2, r=3);
            cylinder(h=body_height/2, r=2);
        }

        // Lower leg
        translate([0, 0, -body_height/2]) {
            rotate([15, 0, 0]) {
                difference() {
                    cylinder(h=body_height/3, r=2.5);
                    cylinder(h=body_height/3, r=1.5);
                }
            }
        }

        // Foot
        translate([0, 0, -body_height/2 - body_height/3]) {
            difference() {
                scale([1.5, 1, 0.5]) sphere(r=3);
                scale([1.5, 1, 0.5]) sphere(r=2);
            }

            // Claws
            for(claw = [-1, 0, 1]) {
                translate([claw * 2, 0, -1]) {
                    rotate([0, 0, claw * 15]) {
                        difference() {
                            cylinder(h=3, r1=0.8, r2=0.3);
                            cylinder(h=3, r1=0.5, r2=0);
                        }
                    }
                }
            }
        }
    }
}

// Create the dragon
hollowglow_dragon();

// Iceglow Dragon - Realistic Medium Complexity
// A crystalline ice dragon with glowing magical ice formations

// Overall dimensions: ~80mm long, ~60mm tall, ~70mm wingspan
// Designed for 3D printing with no overhangs >45 degrees

// Main dragon body module
module dragon_body() {
    color("LightBlue")
    union() {
        // Central torso
        translate([0, 0, 15])
            scale([1, 0.6, 0.8])
            sphere(r=15, $fn=32);

        // Neck
        translate([0, 0, 30])
            rotate([0, 10, 0])
            scale([0.5, 0.5, 1.2])
            cylinder(r1=8, r2=6, h=20, $fn=32);

        // Head
        translate([0, 0, 50])
            rotate([0, 10, 0])
            scale([0.8, 0.7, 1])
            sphere(r=10, $fn=32);

        // Tail
        translate([0, 0, 10])
            rotate([0, -10, 0])
            scale([1, 1, 2])
            cylinder(r1=10, r2=3, h=30, $fn=32);
    }
}

// Crystal ice spikes along back
module ice_spikes() {
    color("DeepSkyBlue")
    for(i = [0:3]) {
        translate([-15 + i * 10, 0, 35 + i * 2])
            rotate([0, -10, 0])
            scale([1, 1, 1.5])
            cylinder(r1=3, r2=1, h=8, $fn=6);
    }
}

// Wings with crystalline structure
module wings() {
    color("LightCyan")
    union() {
        // Left wing
        translate([-10, 0, 35])
            rotate([0, 0, -30])
            scale([1, 0.1, 1])
            sphere(r=25, $fn=32);

        // Right wing
        translate([10, 0, 35])
            rotate([0, 0, 30])
            scale([1, 0.1, 1])
            sphere(r=25, $fn=32);

        // Wing membrane supports
        for(i = [0:2]) {
            // Left wing ribs
            translate([-25 + i * 8, 0, 35 + i * 5])
                rotate([0, -20, -30])
                cylinder(r=1, h=20, $fn=8);

            // Right wing ribs
            translate([25 - i * 8, 0, 35 + i * 5])
                rotate([0, -20, 30])
                cylinder(r=1, h=20, $fn=8);
        }
    }
}

// Legs with clawed feet
module legs() {
    color("LightBlue")
    union() {
        // Front left leg
        translate([-8, 0, 20])
            rotate([0, -30, 0])
            cylinder(r=3, h=15, $fn=16);

        // Front left foot
        translate([-10, 0, 5])
            union() {
                sphere(r=4, $fn=16);
                for(i = [-1, 0, 1]) {
                    translate([i * 2, 0, -2])
                        rotate([0, 0, i * 15])
                        cylinder(r=0.8, h=4, $fn=8);
                }
            }

        // Front right leg
        translate([8, 0, 20])
            rotate([0, 30, 0])
            cylinder(r=3, h=15, $fn=16);

        // Front right foot
        translate([10, 0, 5])
            union() {
                sphere(r=4, $fn=16);
                for(i = [-1, 0, 1]) {
                    translate([i * 2, 0, -2])
                        rotate([0, 0, i * 15])
                        cylinder(r=0.8, h=4, $fn=8);
                }
            }

        // Back left leg
        translate([-5, 0, 10])
            rotate([0, -30, 0])
            cylinder(r=3.5, h=12, $fn=16);

        // Back left foot
        translate([-7, 0, 0])
            union() {
                sphere(r=4.5, $fn=16);
                for(i = [-1, 0, 1]) {
                    translate([i * 2.2, 0, -2])
                        rotate([0, 0, i * 15])
                        cylinder(r=1, h=4, $fn=8);
                }
            }

        // Back right leg
        translate([5, 0, 10])
            rotate([0, 30, 0])
            cylinder(r=3.5, h=12, $fn=16);

        // Back right foot
        translate([7, 0, 0])
            union() {
                sphere(r=4.5, $fn=16);
                for(i = [-1, 0, 1]) {
                    translate([i * 2.2, 0, -2])
                        rotate([0, 0, i * 15])
                        cylinder(r=1, h=4, $fn=8);
                }
            }
    }
}

// Horns with ice crystal texture
module horns() {
    color("Azure")
    union() {
        // Left horn
        translate([-5, 0, 58])
            rotate([0, -20, -20])
            cylinder(r1=3, r2=1, h=12, $fn=8);

        // Right horn
        translate([5, 0, 58])
            rotate([0, -20, 20])
            cylinder(r1=3, r2=1, h=12, $fn=8);

        // Horn crystals
        for(i = [0:2]) {
            translate([-5 - i * 1, 0, 60 + i * 3])
                rotate([0, -20 + i * 10, -20])
                scale([0.3, 0.3, 1])
                cylinder(r=2, h=3, $fn=6);

            translate([5 + i * 1, 0, 60 + i * 3])
                rotate([0, -20 + i * 10, 20])
                scale([0.3, 0.3, 1])
                cylinder(r=2, h=3, $fn=6);
        }
    }
}

// Glowing ice crystals on body
module glowing_crystals() {
    color("Cyan")
    for(i = [0:5]) {
        angle = i * 60;
        radius = 12;

        translate([cos(angle) * radius, sin(angle) * radius, 25])
            rotate([0, 0, angle])
            scale([1, 1, 1.5])
            sphere(r=2, $fn=6);
    }

    // Larger chest crystal
    translate([0, 5, 25])
        scale([1.2, 1.2, 1.8])
        sphere(r=3, $fn=8);
}

// Eyes with magical glow
module eyes() {
    color("White")
    union() {
        // Left eye socket
        translate([-3, -5, 52])
            sphere(r=2, $fn=16);

        // Right eye socket
        translate([3, -5, 52])
            sphere(r=2, $fn=16);

        // Glowing pupils
        color("Cyan")
        translate([-3, -6, 52])
            sphere(r=1, $fn=8);

        translate([3, -6, 52])
            sphere(r=1, $fn=8);
    }
}

// Assemble the complete Iceglow Dragon
translate([0, 0, 0])
    union() {
        dragon_body();
        ice_spikes();
        wings();
        legs();
        horns();
        glowing_crystals();
        eyes();
