// Jadeglow - A mythical serpentine dragon of jade essence
// Dimensions: ~80mm long, 30mm tall
// Realistic style with medium complexity

// Color definitions
jade_color = [0.2, 0.8, 0.4, 0.9];  // Translucent jade green
glow_color = [0.5, 1.0, 0.6, 0.6];  // Soft inner glow
accent_color = [0.1, 0.3, 0.2, 1.0]; // Darker jade accents

// Main body module
module jadeglow_body() {
    union() {
        // Head
        translate([0, 0, 15]) {
            color(jade_color) {
                sphere(8, $fn=32);

                // Snout
                translate([7, 0, -1]) {
                    scale([1.5, 0.8, 0.8]) sphere(5, $fn=24);
                }

                // Horns
                for(i = [-1, 1]) {
                    translate([3, 4*i, 6]) {
                        rotate([0, 15*i, 0]) {
                            scale([0.3, 0.3, 1]) cylinder(h=8, r1=2, r2=0.5, $fn=8);
                        }
                    }
                }
            }

            // Eyes
            for(i = [-1, 1]) {
                translate([5, 3*i, 2]) {
                    color(glow_color) sphere(1.5, $fn=16);
                }
            }
        }

        // Neck
        translate([-5, 0, 12]) {
            color(jade_color) {
                rotate([0, 10, 0]) {
                    cylinder(h=10, r1=6, r2=4, $fn=24);
                }
            }
        }

        // Torso segments
        for(i = [0:3]) {
            translate([-15 - i*10, 0, 12 - i*2]) {
                color(jade_color) {
                    rotate([0, -5*i, 0]) {
                        sphere(6 - i*0.5, $fn=24);
                    }
                }

                // Scales
                for(j = [0:5]) {
                    rotate([0, 0, j*60]) {
                        translate([0, 4, 0]) {
                            color(accent_color) {
                                scale([1, 0.5, 0.3]) sphere(1, $fn=6);
                            }
                        }
                    }
                }
            }
        }

        // Tail
        for(i = [0:5]) {
            translate([-45 - i*6, 0, 8 - i*1.5]) {
                color(jade_color) {
                    scale([1 - i*0.1, 1 - i*0.1, 1 - i*0.1]) {
                        sphere(5 - i*0.5, $fn=16);
                    }
                }
            }
        }
    }
}

// Wings module
module jadeglow_wings() {
    union() {
        for(side = [-1, 1]) {
            translate([-20, side*12, 18]) {
                rotate([0, 0, side*20]) {
                    color(jade_color) {
                        // Main wing structure
                        difference() {
                            scale([1.5, 1, 0.1]) sphere(10, $fn=32);
                            translate([8, 0, -1]) cube([20, 20, 2], center=true);
                        }

                        // Wing bones
                        for(i = [0:2]) {
                            rotate([0, 0, i*45*side]) {
                                translate([5, 0, 0]) {
                                    color(accent_color) {
                                        scale([0.2, 0.2, 1]) cylinder(h=15, r1=1, r2=0.5, $fn=8);
                                    }
                                }
                            }
                        }

                        // Wing membrane details
                        for(i = [0:3]) {
                            rotate([0, 0, i*30*side]) {
                                translate([8, side*3*i, 0]) {
                                    color(glow_color) {
                                        sphere(1, $fn=8);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Legs module
module jadeglow_legs() {
    union() {
        for(side = [-1, 1]) {
            translate([-25, side*8, 5]) {
                color(jade_color) {
                    // Upper leg
                    rotate([0, 0, side*30]) {
                        cylinder(h=10, r1=2, r2=1.5, $fn=12);
                    }

                    // Lower leg
                    translate([-8, side*5, -5]) {
                        rotate([30, 0, side*20]) {
                            cylinder(h=8, r1=1.5, r2=1, $fn=12);
                        }
                    }

                    // Foot
                    translate([-12, side*7, -10]) {
                        scale([1.5, 1, 0.5]) sphere(2, $fn=12);

                        // Claws
                        for(claw = [-1, 0, 1]) {
                            translate([1, claw*0.8, -1]) {
                                color(accent_color) {
                                    rotate([-30, 0, 0]) {
                                        cylinder(h=3, r1=0.5, r2=0.2, $fn=6);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Magical glow orbs along body
module glow_orbs() {
    for(i = [0:7]) {
        translate([-10 - i*7, 0, 12 - abs(i-3.5)*1.5]) {
            color(glow_color) {
                sphere(1.5, $fn=16);
            }
        }
    }
}

// Main assembly
scale(0.8) {
    union() {
        jadeglow_body();
        jadeglow_wings();
        jadeglow_legs();
        glow_orbs();
    }
}

// Kindleglow - A mythical fire spirit phoenix-dragon hybrid
// Medium complexity realistic style with 3D printable features

$fa = 2;
$fs = 0.5;

// Overall dimensions (mm)
creature_length = 80;
creature_height = 45;
creature_width = 35;

// Wing parameters
wing_span = 60;
wing_thickness = 2;

// Main body module
module kindleglow_body() {
    color("darkorange") {
        // Main torso - elongated egg shape
        scale([1.2, 0.8, 0.6]) sphere(d = 30);

        // Chest ridge
        translate([5, 0, 8]) rotate([0, 90, 0]) 
            cylinder(h = 15, r1 = 3, r2 = 1);

        // Hip section
        translate([-15, 0, -2]) scale([0.8, 0.7, 0.5]) sphere(d = 25);
    }
}

// Head with phoenix features
module kindleglow_head() {
    color("orangered") {
        // Main head shape
        translate([20, 0, 12]) rotate([0, -10, 0]) 
            scale([1.3, 0.9, 0.8]) sphere(d = 18);

        // Beak-like snout
        translate([30, 0, 12]) rotate([0, -20, 0])
            cylinder(h = 12, r1 = 4, r2 = 0);

        // Eye sockets
        translate([25, 5, 14]) sphere(d = 3);
        translate([25, -5, 14]) sphere(d = 3);

        // Phoenix crest
        for(i = [0:30:150]) {
            rotate([0, 0, i]) translate([15, 0, 22])
                cylinder(h = 8, r1 = 0.5, r2 = 2);
        }
    }
}

// Dragon wings with membrane structure
module kindleglow_wing(left = true) {
    color("orange") {
        // Wing arm
        mirror([left ? 0 : 1, 0, 0]) {
            translate([0, 15, 10]) rotate([0, -20, -30]) {
                // Main wing bone
                cylinder(h = 25, r1 = 2, r2 = 1);

                // Wing membrane segments
                for(i = [0:2]) {
                    rotate([0, i * 15, 0]) {
                        translate([0, 0, -i * 3]) {
                            difference() {
                                // Membrane
                                translate([10, 0, 0]) 
                                    rotate([0, 90, 0])
                                    scale([0.8, 0.3, 1]) 
                                    sphere(d = wing_span - i * 10);

                                // Cut holes for realistic membrane
                                for(j = [1:3]) {
                                    translate([5, j * 3 - 6, 0])
                                        cylinder(h = 10, r = 0.8);
                                }
                            }
                        }
                    }
                }

                // Wing claw
                translate([25, 0, 0])
                    rotate([0, 90, 0])
                    cylinder(h = 5, r1 = 1.5, r2 = 0.5);
            }
        }
    }
}

// Tail with flame segments
module kindleglow_tail() {
    color("red") {
        // Tail segments
        for(i = [0:4]) {
            translate([-20 - i * 8, 0, -5 + i * 2]) {
                scale([1 - i * 0.15, 1 - i * 0.15, 1 - i * 0.1])
                    sphere(d = 12 - i * 2);
            }
        }

        // Flame trail effect
        color("yellow") {
            for(i = [0:3]) {
                rotate([0, 0, i * 120])
                    translate([-45, 0, 5])
                    scale([0.3, 0.3, 1.5])
                    sphere(d = 8);
            }
        }
    }
}

// Legs with claws
module kindleglow_legs() {
    color("darkred") {
        // Front legs
        for(side = [-1, 1]) {
            translate([10, side * 8, -5]) {
                // Upper leg
                rotate([45, 0, side * 15])
                    cylinder(h = 12, r1 = 2.5, r2 = 2);

                // Lower leg
                translate([8, 0, -8]) rotate([90, 0, 0])
                    cylinder(h = 10, r1 = 2, r2 = 1.5);

                // Foot
                translate([8, 0, -13]) {
                    scale([1.5, 0.8, 0.5]) sphere(d = 6);

                    // Claws
                    for(claw = [-1, 0, 1]) {
                        translate([3, claw * 2, -1]) rotate([0, -30, 0])
                            cylinder(h = 4, r1 = 1, r2 = 0.5);
                    }
                }
            }
        }

        // Rear legs
        for(side = [-1, 1]) {
            translate([-10, side * 6, -3]) {
                rotate([30, 0, side * 20])
                    cylinder(h = 10, r1 = 2.5, r2 = 2);

                translate([7, 0, -6]) rotate([90, 0, 0])
                    cylinder(h = 8, r1 = 2, r2 = 1.5);

                translate([7, 0, -10]) {
                    scale([1.3, 0.7, 0.4]) sphere(d = 5);
                }
            }
        }
    }
}

// Flame aura around body
module kindleglow_flame_aura() {
    color("gold", 0.3) {
        difference() {
            scale([1.1, 1.1, 1.1]) sphere(d = 40);
            sphere(d = 38);
        }

        // Floating embers
        for(i = [0:8]) {
            rotate([i * 45, i * 20, 0])
                translate([20, 0, 5 + sin(i * 45) * 5])
                sphere(d = 1.5);
        }
    }
}

// Assembly
union() {
    kindleglow_body();
    kindleglow_head();
    kindleglow_wing(true);
    kindleglow_wing(false);
    kindleglow_tail();
    kindleglow_legs();
    kindleglow_flame_aura();
}
